import { useEffect, useState } from "react";
import StatsCards from "./StatsCards";
import BarChart from "./BarChart";
import PieChart from "./PieChart";
import TicketList from "./TicketList";
import TicketFilter from "./TicketFilter";

export default function Dashboard() {
  const [stats, setStats] = useState({ total: 0, closed: 0, avgTime: 0 });
  const [dailyData, setDailyData] = useState([]);
  const [priorityData, setPriorityData] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [filteredTickets, setFilteredTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [period, setPeriod] = useState('month');

  const [filters, setFilters] = useState({
    search: "",
    priority: "",
    status: "",
    project: "",
    dateFrom: "",
    dateTo: "",
  });

  useEffect(() => {
    fetchData();
  }, [period]);

  useEffect(() => {
    applyFilters();
  }, [filters, tickets]);

  const fetchData = () => {
    setLoading(true);
    fetch(`http://localhost/tickets-api/dashboard_stats.php?period=${period}`)
      .then((res) => res.json())
      .then((data) => {
        setStats(data.stats || { total: 0, closed: 0, avgTime: 0 });
        setDailyData(data.dailyChart || []);
        setTickets(data.tickets || []);
        setFilteredTickets(data.tickets || []);
        generatePriorityData(data.tickets || []);
      })
      .catch((err) => console.error("Eroare la fetch dashboard:", err))
      .finally(() => setLoading(false));
  };

  const generatePriorityData = (tickets) => {
    const counts = { Critical: 0, High: 0, Medium: 0, Low: 0 };
    tickets.forEach((t) => {
      counts[t.priority_name] = (counts[t.priority_name] || 0) + 1;
    });
    const result = Object.entries(counts)
      .map(([name, value]) => ({ name, value }))
      .filter((item) => item.value > 0);
    setPriorityData(result);
  };

  const applyFilters = () => {
    let filtered = [...tickets];

    if (filters.search.trim()) {
      const term = filters.search.toLowerCase();
      filtered = filtered.filter(
        (t) =>
          t.incident_title?.toLowerCase().includes(term) ||
          t.id?.toString().includes(term)
      );
    }
    if (filters.priority) {
      filtered = filtered.filter((t) => t.priority_name === filters.priority);
    }
    if (filters.status) {
      filtered = filtered.filter((t) => t.status === filters.status);
    }
    if (filters.project) {
      filtered = filtered.filter((t) => t.project?.toLowerCase().includes(filters.project.toLowerCase()));
    }
    if (filters.dateFrom) {
      filtered = filtered.filter((t) => new Date(t.start_date) >= new Date(filters.dateFrom));
    }
    if (filters.dateTo) {
      filtered = filtered.filter((t) => new Date(t.start_date) <= new Date(filters.dateTo));
    }

    setFilteredTickets(filtered);
  };

  const handleFilterChange = (field, value) => {
    setFilters((prev) => ({ ...prev, [field]: value }));
  };

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4 sm:px-10 dark:bg-gray-900">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-center mb-8 text-gray-800 dark:text-gray-100">
          Dashboard Operațional Tickete
        </h1>

        {/* Selector Perioadă */}
        <div className="flex justify-center gap-2 mb-6">
          {["day", "week", "month", "year"].map((p) => (
            <button
              key={p}
              onClick={() => setPeriod(p)}
              className={`px-4 py-2 rounded-full text-sm font-medium transition ${
                period === p
                  ? "bg-blue-600 text-white"
                  : "bg-gray-300 dark:bg-gray-700 text-gray-700 dark:text-gray-300"
              }`}
            >
              {p === "day" ? "Zi" : p === "week" ? "Săptămână" : p === "month" ? "Lună" : "An"}
            </button>
          ))}
        </div>

        {/* KPI Cards */}
        <StatsCards {...stats} />

        {/* Două grafice */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 my-10">
          {/* BarChart Tickete pe Zile */}
          <div className="bg-white dark:bg-gray-800 rounded-lg p-6 shadow">
            <h2 className="text-lg font-semibold text-gray-700 dark:text-gray-200 mb-4">Tickete pe Zile</h2>
            {loading ? (
              <div className="text-center text-gray-400">Se încarcă...</div>
            ) : (
              <BarChart data={dailyData} />
            )}
          </div>

          {/* PieChart Tickete pe Priorități */}
          <div className="bg-white dark:bg-gray-800 rounded-lg p-6 shadow">
            <h2 className="text-lg font-semibold text-gray-700 dark:text-gray-200 mb-4">Distribuție Tickete pe Prioritate</h2>
            {loading ? (
              <div className="text-center text-gray-400">Se încarcă...</div>
            ) : (
              <PieChart data={priorityData} />
            )}
          </div>
        </div>

        {/* Filtrare și Lista de Tickete */}
        <div className="bg-white dark:bg-gray-800 rounded-lg p-6 shadow mb-8">
          <TicketFilter filters={filters} onFilterChange={handleFilterChange} />
        </div>

        {/* Tickete */}
        {loading ? (
          <p className="text-center text-gray-400">Se încarcă ticketele...</p>
        ) : (
          <TicketList tickets={filteredTickets} />
        )}
      </div>
    </div>
  );
}
