import { useEffect, useState } from "react";
import StatsCards from "./StatsCards";
import BarChart from "./BarChart";
import TicketFilter from "./TicketFilter";
import TicketList from "./TicketList";

export default function Dashboard() {
  const [stats, setStats] = useState({ total: 0, resolved: 0, avgTime: 0 });
  const [dailyData, setDailyData] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [filteredTickets, setFilteredTickets] = useState([]);
  const [loading, setLoading] = useState(true);

  const [filters, setFilters] = useState({
    search: "",
    priority: "",
    status: "",
    project: "",
    dateFrom: "",
    dateTo: "",
  });

  const [visibleCount, setVisibleCount] = useState(20); // pentru paginare

  useEffect(() => {
    setLoading(true);
    fetch("http://localhost/tickets-api/dashboard_stats.php")
      .then((res) => res.json())
      .then((data) => {
        setStats(data.stats || { total: 0, resolved: 0, avgTime: 0 });
        setDailyData(data.dailyChart || []);
        setTickets(data.tickets || []);
        setFilteredTickets(data.tickets || []);
      })
      .catch((err) => console.error("Eroare la fetch dashboard:", err))
      .finally(() => setLoading(false));
  }, []);

  useEffect(() => {
    let filtered = [...tickets];

    if (filters.search.trim()) {
      const term = filters.search.toLowerCase();
      filtered = filtered.filter(
        (t) =>
          t.incident_title?.toLowerCase().includes(term) ||
          t.id?.toString().includes(term)
      );
    }

    if (filters.priority) {
      filtered = filtered.filter((t) => t.priority_name === filters.priority);
    }

    if (filters.status) {
      filtered = filtered.filter((t) => t.status === filters.status);
    }

    if (filters.project) {
      filtered = filtered.filter((t) =>
        t.project?.toLowerCase().includes(filters.project.toLowerCase())
      );
    }

    if (filters.dateFrom) {
      filtered = filtered.filter(
        (t) => new Date(t.start_date) >= new Date(filters.dateFrom)
      );
    }

    if (filters.dateTo) {
      filtered = filtered.filter(
        (t) => new Date(t.start_date) <= new Date(filters.dateTo)
      );
    }

    setFilteredTickets(filtered);
    setVisibleCount(20); // când filtrezi, reset la primele 20
  }, [filters, tickets]);

  const loadMoreTickets = () => {
    setVisibleCount((prev) => prev + 20);
  };

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4 sm:px-10">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-800 mb-8 text-center">
          Dashboard Operațional Tickete
        </h1>

        {/* Statistici */}
        <StatsCards {...stats} />

        {/* Grafic pe zile */}
        <div className="bg-white p-6 rounded-lg shadow mb-8">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-lg font-semibold text-gray-700">Tickete pe Zile</h2>
          </div>
          {loading ? (
            <div className="text-center text-gray-400">Se încarcă graficul...</div>
          ) : (
            <BarChart data={dailyData} />
          )}
        </div>

        {/* Filtru */}
        <div className="bg-white p-6 rounded-lg shadow mb-8">
          <h2 className="text-lg font-semibold text-gray-700 mb-4">Filtrare Tickete</h2>
          <TicketFilter filters={filters} onFilterChange={setFilters} />
        </div>

        {/* Lista Tickete */}
        {loading ? (
          <p className="text-center text-gray-500">Se încarcă datele...</p>
        ) : filteredTickets.length > 0 ? (
          <>
            <TicketList tickets={filteredTickets.slice(0, visibleCount)} />
            {visibleCount < filteredTickets.length && (
              <div className="flex justify-center mt-8">
                <button
                  onClick={loadMoreTickets}
                  className="px-6 py-2 bg-gray-800 text-white text-sm rounded-lg hover:bg-gray-700 transition"
                >
                  Afișează mai multe
                </button>
              </div>
            )}
          </>
        ) : (
          <p className="text-center text-gray-500">Nu există tickete pentru filtrele alese.</p>
        )}
      </div>
    </div>
  );
}
