import { useEffect, useState } from "react";

const PRIORITY_COLORS = {
  1: "border-red-600",
  2: "border-orange-400",
  3: "border-yellow-300",
  4: "border-green-400",
};

export default function Dashboard() {
  const [tickets, setTickets] = useState([]);
  const [visibleCount, setVisibleCount] = useState(10);
  const [expandedIds, setExpandedIds] = useState([]);

  useEffect(() => {
    fetch("http://localhost/tickets-api/tickets_latest.php")
      .then((res) => res.json())
      .then((data) => {
        const openTickets = data.filter(ticket => ticket.status !== 'Resolved' && ticket.status !== 'Closed');
        setTickets(openTickets);
      })
      .catch((err) => console.error("Eroare la fetch:", err));
  }, []);

  const toggleExpand = (id) => {
    setExpandedIds(prev =>
      prev.includes(id) ? prev.filter(e => e !== id) : [...prev, id]
    );
  };

  const showMore = () => {
    setVisibleCount(prev => prev + 10);
  };

  return (
    <div className="min-h-screen bg-gray-100 px-4 sm:px-8 py-10">
      <h1 className="text-4xl font-bold mb-8 text-center text-gray-800">Tickete Deschise</h1>

      <div className="flex flex-col gap-6 max-w-4xl mx-auto">
        {tickets.slice(0, visibleCount).map((ticket) => (
          <div
            key={ticket.id}
            className={`bg-white shadow-lg border-l-8 rounded-xl p-6 transition-all duration-300 ${PRIORITY_COLORS[ticket.priority_name] || "border-gray-300"}`}
          >
            <div className="flex justify-between items-start">
              <div>
                <h2 className="text-xl font-semibold text-gray-800">{ticket.incident_title}</h2>
                <p className="text-gray-600 text-sm mt-1">
                  <span className="font-medium">Prioritate:</span> {ticket.priority_name} |{" "}
                  <span className="font-medium">Status:</span> {ticket.status}
                </p>
                <p className="text-gray-600 text-sm">
                  <span className="font-medium">Creat de:</span> {ticket.created_by} |{" "}
                  <span className="font-medium">Start:</span>{" "}
                  {ticket.start_date ? new Date(ticket.start_date).toLocaleDateString("ro-RO") : "-"}
                </p>
              </div>

              <button
                onClick={() => toggleExpand(ticket.id)}
                className="text-sm px-3 py-1 rounded-full bg-blue-600 text-white hover:bg-blue-700 transition"
              >
                {expandedIds.includes(ticket.id) ? "Ascunde -" : "Detalii +"}
              </button>
            </div>

            {expandedIds.includes(ticket.id) && (
              <div className="mt-4 border-t pt-4 grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-gray-700">
                <div><span className="font-semibold">Asignat:</span> {ticket.assigned_person || "-"}</div>
                <div><span className="font-semibold">Echipa:</span> {ticket.team_assigned_person || "-"}</div>
                <div><span className="font-semibold">Proiect:</span> {ticket.project || "-"}</div>
                <div><span className="font-semibold">Comentariu:</span> {ticket.comment || "-"}</div>
                <div><span className="font-semibold">Rezolvare:</span> {ticket.resolution || "-"}</div>
                <div><span className="font-semibold">Închis la:</span> {ticket.closed_date ? new Date(ticket.closed_date).toLocaleDateString("ro-RO") : "-"}</div>
              </div>
            )}
          </div>
        ))}
      </div>

      {visibleCount < tickets.length && (
        <div className="flex justify-center mt-8">
          <button
            onClick={showMore}
            className="px-6 py-2 bg-gray-800 text-white text-sm rounded-lg hover:bg-gray-700 transition"
          >
            Afișează mai multe
          </button>
        </div>
      )}
    </div>
  );
}
