import { useState, useEffect } from "react";
import Dashboard from "./components/Dashboard";
import History from "./components/History";
import AdminPanel from "./components/AdminPanel"; // ðŸ†• import AdminPanel
import ThemeToggle from "./components/ThemeToggle";
import Login from "./components/Login";

export default function App() {
  const [activeTab, setActiveTab] = useState("dashboard");
  const [authenticated, setAuthenticated] = useState(false);
  const [role, setRole] = useState(null); // ðŸ†• rolul utilizatorului (user/admin/superuser)

  useEffect(() => {
    const token = localStorage.getItem("token");
    const storedRole = localStorage.getItem("role");

    setAuthenticated(!!token);
    setRole(storedRole);
  }, []);

  const handleLoginSuccess = (userRole) => {
    setAuthenticated(true);
    setRole(userRole);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    setAuthenticated(false);
    setRole(null);
  };

  if (!authenticated) {
    return <Login onLogin={handleLoginSuccess} />;
  }

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors duration-300">
      <div className="relative flex justify-center gap-4 py-6 bg-white dark:bg-gray-800 shadow">
        {/* Tab buttons */}
        <button
          onClick={() => setActiveTab("dashboard")}
          className={`px-6 py-2 rounded ${activeTab === "dashboard" ? "bg-blue-600 text-white" : "bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300"}`}
        >
          Dashboard
        </button>

        <button
          onClick={() => setActiveTab("history")}
          className={`px-6 py-2 rounded ${activeTab === "history" ? "bg-blue-600 text-white" : "bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300"}`}
        >
          Istoric
        </button>

        {/* DacÄƒ e Admin sau Superuser, afiÈ™Äƒm È™i Admin Panel */}
        {(role === "admin" || role === "superuser") && (
          <button
            onClick={() => setActiveTab("admin")}
            className={`px-6 py-2 rounded ${activeTab === "admin" ? "bg-blue-600 text-white" : "bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300"}`}
          >
            Admin Panel
          </button>
        )}

        {/* Buton Theme + Logout */}
        <div className="absolute top-4 right-4 flex gap-4">
          <ThemeToggle />
          <button onClick={handleLogout} className="bg-red-500 text-white px-4 py-2 rounded">
            Logout
          </button>
        </div>
      </div>

      {/* Tab content */}
      {activeTab === "dashboard" && <Dashboard />}
      {activeTab === "history" && <History />}
      {activeTab === "admin" && (role === "admin" || role === "superuser") && <AdminPanel />}
    </div>
  );
}
