import { useState, useEffect } from "react";
import TicketList from "./TicketList";

export default function History() {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    fetch("http://localhost/tickets-api/dashboard_stats.php?period=all")
      .then((res) => res.json())
      .then((data) => {
        const closedTickets = (data.tickets || []).filter(
          (t) => t.status === "Resolved" || t.status === "Closed"
        );
        setTickets(closedTickets);
      })
      .catch((err) => console.error("Eroare la fetch history:", err))
      .finally(() => setLoading(false));
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4 sm:px-10">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-800 mb-8 text-center">
          Istoric Tickete Rezolvate/Închise
        </h1>

        {loading ? (
          <p className="text-center text-gray-500">Se încarcă datele...</p>
        ) : tickets.length > 0 ? (
          <TicketList tickets={tickets} />
        ) : (
          <p className="text-center text-gray-500">Nu există tickete închise sau rezolvate.</p>
        )}
      </div>
    </div>
  );
}
