import { useEffect, useState } from "react";
import StatsCards from "./StatsCards";
import TicketList from "./TicketList";
import TicketFilter from "./TicketFilter";
import BarChart from "./BarChart";
import PieChart from "./PieChart";

export default function Dashboard() {
  const [stats, setStats] = useState({ total: 0, resolved: 0, avgTime: 0 });
  const [dailyData, setDailyData] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [filteredTickets, setFilteredTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [period, setPeriod] = useState("month"); // default luna

  const [filters, setFilters] = useState({
    search: "",
    priority: "",
    status: "",
    project: "",
    dateFrom: "",
    dateTo: "",
  });

  useEffect(() => {
    setLoading(true);
    fetch(`http://localhost/tickets-api/dashboard_stats.php?period=${period}`)
      .then((res) => res.json())
      .then((data) => {
        setStats(data.stats || { total: 0, resolved: 0, avgTime: 0 });
        setDailyData(data.dailyChart || []);
        setTickets(data.tickets || []);
        setFilteredTickets(data.tickets || []);
      })
      .catch((err) => console.error("Eroare la fetch dashboard:", err))
      .finally(() => setLoading(false));
  }, [period]);

  useEffect(() => {
    let filtered = [...tickets];

    if (filters.search.trim()) {
      const term = filters.search.toLowerCase();
      filtered = filtered.filter(
        (t) =>
          t.incident_title?.toLowerCase().includes(term) ||
          t.id?.toString().includes(term)
      );
    }
    if (filters.priority) {
      filtered = filtered.filter((t) => t.priority_name === filters.priority);
    }
    if (filters.status) {
      filtered = filtered.filter((t) => t.status === filters.status);
    }
    if (filters.project) {
      filtered = filtered.filter((t) =>
        t.project?.toLowerCase().includes(filters.project.toLowerCase())
      );
    }
    if (filters.dateFrom) {
      filtered = filtered.filter(
        (t) => new Date(t.start_date) >= new Date(filters.dateFrom)
      );
    }
    if (filters.dateTo) {
      filtered = filtered.filter(
        (t) => new Date(t.start_date) <= new Date(filters.dateTo)
      );
    }

    setFilteredTickets(filtered);
  }, [filters, tickets]);

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 py-10 px-4 sm:px-10">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-800 dark:text-white mb-8 text-center">
          Dashboard Operațional Tickete
        </h1>

        {/* KPIs */}
        <StatsCards {...stats} />

        {/* Butoane Perioadă */}
        <div className="flex justify-center gap-2 mb-6">
          {["day", "week", "month", "year"].map((p) => (
            <button
              key={p}
              onClick={() => setPeriod(p)}
              className={`text-sm px-4 py-1 rounded-full ${
                period === p ? "bg-blue-600 text-white" : "bg-gray-200 dark:bg-gray-700 dark:text-gray-300"
              }`}
            >
              {p === "day" && "Zi"}
              {p === "week" && "Săptămână"}
              {p === "month" && "Lună"}
              {p === "year" && "An"}
            </button>
          ))}
        </div>

        {/* Charturi */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
            <h2 className="text-lg font-semibold text-gray-700 dark:text-gray-200 mb-4">
              Tickete pe Zile
            </h2>
            {loading ? (
              <div className="text-center text-gray-400">Se încarcă graficul...</div>
            ) : (
              <BarChart data={dailyData} />
            )}
          </div>

          <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
            <h2 className="text-lg font-semibold text-gray-700 dark:text-gray-200 mb-4">
              Tickete pe Prioritate
            </h2>
            {loading ? (
              <div className="text-center text-gray-400">Se încarcă graficul...</div>
            ) : (
              <PieChart data={dailyData} />
            )}
          </div>
        </div>

        {/* Filtru de Tickete */}
        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow mb-8">
          <h2 className="text-lg font-semibold text-gray-700 dark:text-gray-200 mb-4">Filtrare Tickete</h2>
          <TicketFilter filters={filters} setFilters={setFilters} />
        </div>

        {/* Lista Tickete */}
        {loading ? (
          <p className="text-center text-gray-500">Se încarcă datele...</p>
        ) : filteredTickets.length > 0 ? (
          <TicketList tickets={filteredTickets} />
        ) : (
          <p className="text-center text-gray-500">Nu există tickete pentru criteriile alese.</p>
        )}
      </div>
    </div>
  );
}
