import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from "react-router-dom";
import Dashboard from "./components/Dashboard";
import History from "./components/History";
import AdminPanel from "./components/AdminPanel";
import ThemeToggle from "./components/ThemeToggle";
import Login from "./components/Login";

export default function App() {
  const [authenticated, setAuthenticated] = useState(false);
  const [role, setRole] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    const savedRole = localStorage.getItem("role");
    setAuthenticated(!!token);
    setRole(savedRole);
  }, []);

  const handleLoginSuccess = (userRole) => {
    setAuthenticated(true);
    setRole(userRole);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    setAuthenticated(false);
    setRole("");
  };

  if (!authenticated) {
    return <Login onLogin={handleLoginSuccess} />;
  }

  return (
    <Router>
      <div className="min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors duration-300">
        {/* Navbar */}
        <div className="relative flex justify-center gap-4 py-6 bg-white dark:bg-gray-800 shadow">
          <Link
            to="/"
            className="px-6 py-2 rounded bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-blue-600 hover:text-white"
          >
            Dashboard
          </Link>
          <Link
            to="/history"
            className="px-6 py-2 rounded bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-blue-600 hover:text-white"
          >
            Istoric
          </Link>
          {(role === "admin" || role === "superuser") && (
            <Link
              to="/admin"
              className="px-6 py-2 rounded bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-green-600 hover:text-white"
            >
              Admin Panel
            </Link>
          )}

          {/* Theme + Logout */}
          <div className="absolute top-4 right-4 flex gap-4">
            <ThemeToggle />
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
            >
              Logout
            </button>
          </div>
        </div>

        {/* Pagini */}
        <Routes>
          <Route path="/" element={<Dashboard />} />
          <Route path="/history" element={<History />} />
          <Route
            path="/admin"
            element={
              role === "admin" || role === "superuser" ? (
                <AdminPanel />
              ) : (
                <Navigate to="/" />
              )
            }
          />
        </Routes>
      </div>
    </Router>
  );
}
