// src/components/TicketList.js
import React, { useState } from "react";

const PRIORITY_COLORS = {
  Critical: "border-red-600 bg-red-50",
  High: "border-orange-400 bg-orange-50",
  Medium: "border-yellow-400 bg-yellow-50",
  Low: "border-green-400 bg-green-50",
};

export default function TicketList({ tickets }) {
  const [visibleCount, setVisibleCount] = useState(20);
  const [expandedIds, setExpandedIds] = useState([]);

  const toggleExpand = (id) => {
    setExpandedIds((prev) =>
      prev.includes(id) ? prev.filter((e) => e !== id) : [...prev, id]
    );
  };

  const showMore = () => {
    setVisibleCount((prev) => prev + 20);
  };

  return (
    <div className="flex flex-col gap-4">
      {tickets.slice(0, visibleCount).map((ticket) => (
        <div
          key={ticket.id}
          className={`border-l-4 rounded-lg p-4 shadow hover:shadow-md transition relative ${PRIORITY_COLORS[ticket.priority_name] || "border-gray-300 bg-white"}`}
        >
          <div className="flex justify-between items-start">
            <div>
              <h2 className="text-lg font-semibold text-gray-800">{ticket.incident_title}</h2>
              <p className="text-gray-600 text-sm">
                <b>ID:</b> {ticket.id} | <b>Prioritate:</b> {ticket.priority_name} | <b>Status:</b> {ticket.status}
              </p>
              <p className="text-gray-500 text-xs">
                <b>Start Date:</b> {ticket.start_date || "-"}
              </p>
              {ticket.project && (
                <p className="text-gray-500 text-xs">
                  <b>Proiect:</b> {ticket.project}
                </p>
              )}
            </div>

            <button
              onClick={() => toggleExpand(ticket.id)}
              className="text-sm px-3 py-1 rounded-full bg-blue-600 text-white hover:bg-blue-700 transition"
            >
              {expandedIds.includes(ticket.id) ? "Ascunde -" : "Detalii +"}
            </button>
          </div>

          {expandedIds.includes(ticket.id) && (
            <div className="mt-4 border-t pt-4 text-sm text-gray-700">
              {/* Poți adăuga aici mai multe detalii extinse */}
              <p><b>Status:</b> {ticket.status}</p>
              <p><b>Prioritate:</b> {ticket.priority_name}</p>
              <p><b>Data Creării:</b> {ticket.start_date}</p>
              {ticket.project && <p><b>Proiect:</b> {ticket.project}</p>}
            </div>
          )}
        </div>
      ))}

      {visibleCount < tickets.length && (
        <div className="flex justify-center mt-6">
          <button
            onClick={showMore}
            className="px-6 py-2 bg-gray-800 text-white text-sm rounded-lg hover:bg-gray-700 transition"
          >
            Afișează mai multe
          </button>
        </div>
      )}
    </div>
  );
}
