import { useState, useEffect } from "react";
import useAuth from "../hooks/useAuth";
import { useNavigate } from "react-router-dom";

export default function AdminPanel() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user || (user.role !== "admin" && user.role !== "superuser")) {
      navigate("/"); // dacă nu e admin/superuser, îl trimitem înapoi la dashboard
    }
  }, [user, navigate]);

  useEffect(() => {
    setLoading(true);
    fetch("http://localhost/tickets-api/tickets.php")
      .then((res) => res.json())
      .then((data) => {
        setTickets(data || []);
      })
      .catch((err) => console.error("Eroare la fetch tickete:", err))
      .finally(() => setLoading(false));
  }, []);

  const handleFieldChange = (id, field, value) => {
    setTickets((prev) =>
      prev.map((ticket) =>
        ticket.id === id ? { ...ticket, [field]: value } : ticket
      )
    );
  };

  const handleSave = (ticket) => {
    fetch(`http://localhost/tickets-api/update_ticket.php`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(ticket),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) {
          alert("Ticket actualizat cu succes!");
        } else {
          alert("Eroare la actualizare ticket.");
        }
      })
      .catch((err) => console.error("Eroare la update:", err));
  };

  if (!user) return null; // dacă încă nu e userul setat

  return (
    <div className="p-8 bg-gray-100 dark:bg-gray-900 min-h-screen">
      <h1 className="text-3xl font-bold mb-8 text-center text-gray-800 dark:text-gray-100">
        Admin Panel - Gestionare Tickete
      </h1>

      {loading ? (
        <p className="text-center text-gray-500 dark:text-gray-400">Se încarcă ticketele...</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white dark:bg-gray-800 shadow-md rounded-lg overflow-hidden">
            <thead>
              <tr className="bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 text-left">
                <th className="py-3 px-6">ID</th>
                <th className="py-3 px-6">Titlu</th>
                <th className="py-3 px-6">Status</th>
                <th className="py-3 px-6">Prioritate</th>
                <th className="py-3 px-6">Assigned</th>
                <th className="py-3 px-6">Acțiuni</th>
              </tr>
            </thead>
            <tbody>
              {tickets.map((ticket) => (
                <tr key={ticket.id} className="border-b border-gray-300 dark:border-gray-700">
                  <td className="py-3 px-6">{ticket.id}</td>
                  <td className="py-3 px-6">{ticket.incident_title}</td>

                  {/* Status Select */}
                  <td className="py-3 px-6">
                    <select
                      value={ticket.status}
                      onChange={(e) => handleFieldChange(ticket.id, "status", e.target.value)}
                      className="p-2 rounded border dark:bg-gray-700 dark:text-gray-300"
                    >
                      <option value="Open">Open</option>
                      <option value="In Progress">In Progress</option>
                      <option value="Pending">Pending</option>
                      <option value="Closed">Closed</option>
                    </select>
                  </td>

                  {/* Priority Select */}
                  <td className="py-3 px-6">
                    <select
                      value={ticket.priority_name}
                      onChange={(e) => handleFieldChange(ticket.id, "priority_name", e.target.value)}
                      className="p-2 rounded border dark:bg-gray-700 dark:text-gray-300"
                    >
                      <option value="Critical">Critical</option>
                      <option value="High">High</option>
                      <option value="Medium">Medium</option>
                      <option value="Low">Low</option>
                    </select>
                  </td>

                  {/* Assigned Person */}
                  <td className="py-3 px-6">
                    <input
                      type="text"
                      value={ticket.assigned_person || ""}
                      onChange={(e) => handleFieldChange(ticket.id, "assigned_person", e.target.value)}
                      className="p-2 rounded border w-full dark:bg-gray-700 dark:text-gray-300"
                    />
                  </td>

                  {/* Save Button */}
                  <td className="py-3 px-6">
                    <button
                      onClick={() => handleSave(ticket)}
                      className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
                    >
                      Salvează
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
