import { Pie } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js";

ChartJS.register(ArcElement, Tooltip, Legend);

export default function TicketStatusPieChart({ data }) {
  if (!data || data.length === 0) return <p className="text-gray-400">Nu există date pentru grafic.</p>;

  const colors = [
    "#3B82F6", // Open - Albastru
    "#F59E0B", // In Progress - Portocaliu
    "#10B981", // Resolved - Verde
    "#EF4444", // Closed - Roșu
    "#8B5CF6", // Pending - Violet
    "#EC4899", // Rejected - Roz
  ];

  const labels = data.map((item) => item.status || "Necunoscut");
  const counts = data.map((item) => item.count);

  const total = counts.reduce((acc, val) => acc + val, 0);

  const backgroundColors = labels.map((_, index) => colors[index % colors.length]);

  const chartData = {
    labels,
    datasets: [
      {
        label: "Nr Tickete",
        data: counts,
        backgroundColor: backgroundColors,
        borderColor: "#ffffff",
        borderWidth: 2,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false, // => Permite controlul manual al mărimii
    plugins: {
      tooltip: {
        callbacks: {
          label: function (tooltipItem) {
            const value = tooltipItem.raw;
            const percentage = ((value / total) * 100).toFixed(1);
            return `${value} Tickete (${percentage}%)`;
          },
        },
      },
      legend: {
        position: "bottom",
        labels: {
          color: "#374151", // text color
          padding: 20,
        },
      },
    },
    animation: {
      animateRotate: true,
      animateScale: true,
    },
  };

  return (
    <div className="w-[300px] h-[300px] mx-auto">
      <Pie data={chartData} options={options} />
    </div>
  );
}
