import { useState, useEffect } from "react";
import StatsCards from "./StatsCards";
import TicketList from "./TicketList";
import TicketStatusPieChart from "./TicketStatusPieChart";
import AverageTimePerTeamChart from "./AverageTimePerTeamChart";
import { useNavigate } from "react-router-dom"; // Navigare spre Admin Panel

export default function Dashboard() {
  const [period, setPeriod] = useState("day");
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({ total: 0, closed: 0, avgTime: 0 });
  const [tickets, setTickets] = useState([]);
  const [visibleTickets, setVisibleTickets] = useState(20);

  const navigate = useNavigate();
  const role = localStorage.getItem("role");

  useEffect(() => {
    fetchData();
  }, [period]);

  const fetchData = () => {
    setLoading(true);
    fetch(`http://localhost/tickets-api/dashboard_stats.php?period=${period}`)
      .then((res) => res.json())
      .then((data) => {
        setStats(data.stats || { total: 0, closed: 0, avgTime: 0 });
        setTickets(data.tickets || []);
      })
      .catch((err) => console.error("Eroare la fetch dashboard:", err))
      .finally(() => setLoading(false));
  };

  const handleLoadMore = () => {
    setVisibleTickets((prev) => prev + 20);
  };

  return (
    <div className="p-6">

      {/* Butoane Perioadă */}
      <div className="flex justify-center gap-4 mb-8">
        {["day", "week", "month", "year"].map((p) => (
          <button
            key={p}
            onClick={() => setPeriod(p)}
            className={`px-4 py-2 text-sm rounded-full ${
              period === p
                ? "bg-blue-600 text-white"
                : "bg-gray-300 dark:bg-gray-700 text-gray-800 dark:text-gray-300"
            }`}
          >
            {p === "day" ? "Azi" : p === "week" ? "Săptămână" : p === "month" ? "Lună" : "An"}
          </button>
        ))}
      </div>

      {/* Buton Admin Panel pentru Superuser */}
      {role === "superuser" && (
        <div className="flex justify-center mb-8">
          <button
            onClick={() => navigate("/admin-panel")}
            className="px-6 py-2 bg-green-600 text-white rounded hover:bg-green-700"
          >
            Intră în Admin Panel
          </button>
        </div>
      )}

      {/* Statistici */}
      <StatsCards stats={stats} />

      {/* Grafic Status */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-12">
        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
          <h2 className="text-lg font-semibold mb-6 text-center text-gray-700 dark:text-gray-300">Status Tickete</h2>
          {loading ? (
            <p className="text-center text-gray-400">Se încarcă...</p>
          ) : (
            <TicketStatusPieChart data={stats.statusChart || []} height={400} />
          )}
        </div>
        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
          <h2 className="text-lg font-semibold mb-6 text-center text-gray-700 dark:text-gray-300">Tickete pe Echipă</h2>
          {loading ? (
            <p className="text-center text-gray-400">Se încarcă...</p>
          ) : (
            <AverageTimePerTeamChart data={stats.teamChart || []} height={450} />
          )}
        </div>
      </div>

      {/* Lista Tickete */}
      {loading ? (
        <p className="text-center text-gray-400">Se încarcă ticketele...</p>
      ) : (
        <>
          <TicketList tickets={tickets.slice(0, visibleTickets)} />
          {visibleTickets < tickets.length && (
            <div className="flex justify-center mt-6">
              <button
                onClick={handleLoadMore}
                className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
              >
                Încarcă Mai Multe
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
}
