import { useEffect, useState } from "react";
import StatsCards from "./StatsCards";
import BarChart from "./BarChart";
import TicketFilter from "./TicketFilter";
import TicketList from "./TicketList";

export default function Dashboard() {
  const [stats, setStats] = useState({ total: 0, resolved: 0, avgTime: 0 });
  const [dailyData, setDailyData] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [filteredTickets, setFilteredTickets] = useState([]);
  const [filters, setFilters] = useState({
    search: "",
    priority: "",
    status: "",
    project: "",
  });
  const [period, setPeriod] = useState("month"); // default luna
  const [loading, setLoading] = useState(true);
  const [visibleCount, setVisibleCount] = useState(20);

  // Fetch dashboard data
  useEffect(() => {
    setLoading(true);
    fetch(`http://localhost/tickets-api/dashboard_stats.php?period=${period}`)
      .then((res) => res.json())
      .then((data) => {
        setStats(data.stats || { total: 0, resolved: 0, avgTime: 0 });
        setDailyData(data.dailyChart || []);
        setTickets(data.tickets || []);
        setFilteredTickets(data.tickets || []);
        setVisibleCount(20);
      })
      .catch((err) => console.error("Eroare la fetch dashboard:", err))
      .finally(() => setLoading(false));
  }, [period]);

  // Filtering
  useEffect(() => {
    let filtered = [...tickets];

    if (filters.search.trim()) {
      const term = filters.search.toLowerCase();
      filtered = filtered.filter(
        (t) =>
          t.incident_title?.toLowerCase().includes(term) ||
          t.id?.toString().includes(term)
      );
    }

    if (filters.priority) {
      filtered = filtered.filter((t) => t.priority_name === filters.priority);
    }

    if (filters.status) {
      filtered = filtered.filter((t) => t.status === filters.status);
    }

    if (filters.project) {
      filtered = filtered.filter((t) =>
        t.project?.toLowerCase().includes(filters.project.toLowerCase())
      );
    }

    setFilteredTickets(filtered);
    setVisibleCount(20);
  }, [filters, tickets]);

  const showMoreTickets = () => {
    setVisibleCount((prev) => prev + 20);
  };

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4 sm:px-10 dark:bg-gray-900">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-800 dark:text-gray-100 mb-8 text-center">
          Dashboard Operațional Tickete
        </h1>

        {/* Statistici */}
        <div className="flex justify-center mb-6 space-x-2">
          {["day", "week", "month", "year"].map((p) => (
            <button
              key={p}
              onClick={() => setPeriod(p)}
              className={`px-3 py-1 rounded-full text-sm ${
                period === p
                  ? "bg-blue-600 text-white"
                  : "bg-gray-300 dark:bg-gray-700 text-gray-800 dark:text-gray-200"
              }`}
            >
              {p === "day" && "Azi"}
              {p === "week" && "Săptămână"}
              {p === "month" && "Lună"}
              {p === "year" && "An"}
            </button>
          ))}
        </div>

        <StatsCards {...stats} />

        {/* Grafic pe zile */}
        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow mb-8">
          <h2 className="text-lg font-semibold text-gray-700 dark:text-gray-200 mb-4">
            Tickete pe Zile
          </h2>
          {loading ? (
            <div className="text-center text-gray-400">Se încarcă graficul...</div>
          ) : (
            <BarChart data={dailyData} />
          )}
        </div>

        {/* Filtrare */}
        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow mb-8">
          <h2 className="text-lg font-semibold text-gray-700 dark:text-gray-200 mb-4">Filtrare Tickete</h2>
          <TicketFilter filters={filters} onFilterChange={setFilters} />
        </div>

        {/* Lista Tickete */}
        {loading ? (
          <p className="text-center text-gray-500 dark:text-gray-400">Se încarcă datele...</p>
        ) : filteredTickets.length > 0 ? (
          <>
            <TicketList tickets={filteredTickets.slice(0, visibleCount)} />
            {visibleCount < filteredTickets.length && (
              <div className="flex justify-center mt-6">
                <button
                  onClick={showMoreTickets}
                  className="px-6 py-2 rounded bg-blue-600 text-white hover:bg-blue-700"
                >
                  Afișează mai multe
                </button>
              </div>
            )}
          </>
        ) : (
          <p className="text-center text-gray-500 dark:text-gray-400">Nu există tickete pentru filtrele alese.</p>
        )}
      </div>
    </div>
  );
}
