// src/components/StatsCards.js
import React from "react";

export default function StatsCards({ stats }) {
  return (
    <div className="grid grid-cols-1 md:grid-cols-5 gap-6 mb-10">
      
      {/* Total Tickete */}
      <div className="bg-white dark:bg-gray-800 p-4 rounded-lg shadow text-center border-l-4 border-blue-600">
        <h2 className="text-sm text-gray-500 dark:text-gray-400">Total Tickete</h2>
        <p className="text-2xl font-bold text-gray-800 dark:text-gray-100">{stats.total ?? 0}</p>
      </div>

      {/* Tickete Închise */}
      <div className="bg-white dark:bg-gray-800 p-4 rounded-lg shadow text-center border-l-4 border-green-600">
        <h2 className="text-sm text-gray-500 dark:text-gray-400">Tickete Închise</h2>
        <p className="text-2xl font-bold text-gray-800 dark:text-gray-100">{stats.closed ?? 0}</p>
      </div>

      {/* Timp Mediu Rezolvare */}
      <div className="bg-white dark:bg-gray-800 p-4 rounded-lg shadow text-center border-l-4 border-yellow-400">
        <h2 className="text-sm text-gray-500 dark:text-gray-400">Timp Mediu Rezolvare (h)</h2>
        <p className="text-2xl font-bold text-gray-800 dark:text-gray-100">{stats.avgTime ? stats.avgTime.toFixed(1) : 0}</p>
      </div>

      {/* Tickete Overdue */}
      <div className="bg-white dark:bg-gray-800 p-4 rounded-lg shadow text-center border-l-4 border-red-500">
        <h2 className="text-sm text-gray-500 dark:text-gray-400">Tickete Overdue</h2>
        <p className="text-2xl font-bold text-gray-800 dark:text-gray-100">{stats.overdue ?? 0}</p>
      </div>

      {/* % Tickete în SLA */}
      <div className="bg-white dark:bg-gray-800 p-4 rounded-lg shadow text-center border-l-4 border-purple-500">
        <h2 className="text-sm text-gray-500 dark:text-gray-400">% Tickete Rezolvate în SLA</h2>
        <p className="text-2xl font-bold text-gray-800 dark:text-gray-100">{stats.sla_percentage ?? 0}%</p>
      </div>

    </div>
  );
}
