import { useState, useEffect } from "react";
import StatsCards from "./StatsCards";
import TicketFilter from "./TicketFilter";
import TicketList from "./TicketList";
import BarChart from "./BarChart";
import { FaTicketAlt, FaCheckCircle, FaExclamationTriangle, FaClock } from "react-icons/fa";

export default function Dashboard() {
  const [stats, setStats] = useState({ total: 0, open: 0, closed: 0, overdue: 0, avgTime: 0 });
  const [dailyData, setDailyData] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [filteredTickets, setFilteredTickets] = useState([]);
  const [filters, setFilters] = useState({
    search: "",
    priority: "",
    status: "",
    project: "",
    dateFrom: "",
    dateTo: "",
  });
  const [loading, setLoading] = useState(true);
  const [timeFilter, setTimeFilter] = useState("month");

  useEffect(() => {
    setLoading(true);
    fetch(`http://localhost/tickets-api/dashboard_stats.php?period=${timeFilter}`)
      .then((res) => res.json())
      .then((data) => {
        setStats(data.stats || {});
        setDailyData(data.dailyChart || []);
        setTickets(data.tickets || []);
        setFilteredTickets(data.tickets || []);
      })
      .catch((err) => console.error("Eroare la fetch dashboard:", err))
      .finally(() => setLoading(false));
  }, [timeFilter]);

  useEffect(() => {
    let filtered = [...tickets];

    if (filters.search.trim()) {
      const term = filters.search.toLowerCase();
      filtered = filtered.filter(
        (t) =>
          t.incident_title?.toLowerCase().includes(term) ||
          t.id?.toString().includes(term)
      );
    }

    if (filters.priority) {
      filtered = filtered.filter((t) => t.priority_name === filters.priority);
    }

    if (filters.status) {
      filtered = filtered.filter((t) => t.status === filters.status);
    }

    if (filters.project) {
      filtered = filtered.filter((t) =>
        t.project?.toLowerCase().includes(filters.project.toLowerCase())
      );
    }

    if (filters.dateFrom) {
      filtered = filtered.filter((t) => new Date(t.start_date) >= new Date(filters.dateFrom));
    }

    if (filters.dateTo) {
      filtered = filtered.filter((t) => new Date(t.start_date) <= new Date(filters.dateTo));
    }

    setFilteredTickets(filtered);
  }, [filters, tickets]);

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4 sm:px-10">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-800 mb-8 text-center">Dashboard Operațional Tickete</h1>

        {/* KPIs principale */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <StatsCards
            title="Total Tickete"
            value={stats.total || 0}
            icon={<FaTicketAlt className="text-blue-600 text-3xl" />}
          />
          <StatsCards
            title="Tickete Deschise"
            value={stats.open || 0}
            icon={<FaExclamationTriangle className="text-yellow-500 text-3xl" />}
          />
          <StatsCards
            title="Tickete Închise"
            value={stats.closed || 0}
            icon={<FaCheckCircle className="text-green-600 text-3xl" />}
          />
          <StatsCards
            title="Timp Mediu Rezolvare (h)"
            value={stats.avgTime ? stats.avgTime.toFixed(1) : 0}
            icon={<FaClock className="text-purple-600 text-3xl" />}
          />
        </div>

        {/* Selectare Perioadă + Grafic pe zile */}
        <div className="bg-white p-6 rounded-lg shadow mb-8">
          <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-6">
            <h2 className="text-lg font-semibold text-gray-700">Tickete pe Zile</h2>
            <select
              value={timeFilter}
              onChange={(e) => setTimeFilter(e.target.value)}
              className="border p-2 rounded-md"
            >
              <option value="day">Azi</option>
              <option value="week">Ultima Săptămână</option>
              <option value="month">Ultima Lună</option>
            </select>
          </div>
          {loading ? (
            <div className="text-center text-gray-500">Se încarcă graficul...</div>
          ) : (
            <BarChart data={dailyData} />
          )}
        </div>

        {/* Filtre tickete */}
        <div className="bg-white p-6 rounded-lg shadow mb-8">
          <TicketFilter filters={filters} onFilterChange={setFilters} />
        </div>

        {/* Lista tickete */}
        {loading ? (
          <div className="text-center text-gray-500">Se încarcă ticketele...</div>
        ) : (
          <TicketList tickets={filteredTickets} />
        )}
      </div>
    </div>
  );
}
