import {
  PieChart as RechartsPieChart,
  Pie,
  Cell,
  Tooltip,
  ResponsiveContainer
} from "recharts";

// pick a distinct color per status
const COLORS = {
  Open: "#3182ce",
  "In Progress": "#dd6b20",
  Pending: "#d69e2e",
  Closed: "#38a169",
  Resolved: "#805ad5",
};

export default function TicketStatusPieChart({ data = [], height = 380 }) {
  return (
    <ResponsiveContainer width="100%" height={height}>
      <RechartsPieChart>
        <Pie
          data={data}
          dataKey="count"
          nameKey="status"
          cx="50%"
          cy="50%"
          outerRadius={80}
          label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
        >
          {data.map((entry) => (
            <Cell
              key={entry.status}
              fill={COLORS[entry.status] || "#718096"}
            />
          ))}
        </Pie>
        <Tooltip formatter={(value, name) => [value, name]} />
      </RechartsPieChart>
    </ResponsiveContainer>
  );
}
