import { useState } from "react";

const PRIORITY_COLORS = {
  Critical: "border-red-600",
  High: "border-orange-400",
  Medium: "border-yellow-400",
  Low: "border-green-400",
};

export default function TicketList({ tickets }) {
  const [expandedIds, setExpandedIds] = useState([]);
  const [visibleCount, setVisibleCount] = useState(20);

  const toggleExpand = (id) => {
    setExpandedIds((prev) =>
      prev.includes(id) ? prev.filter((e) => e !== id) : [...prev, id]
    );
  };

  const loadMore = () => {
    setVisibleCount((prev) => prev + 20);
  };

  return (
    <div className="flex flex-col gap-6">
      {tickets.slice(0, visibleCount).map((ticket) => (
        <div
          key={ticket.id}
          className={`bg-white shadow-lg border-l-8 rounded-lg p-6 transition-all duration-300 ${
            PRIORITY_COLORS[ticket.priority_name] || "border-gray-300"
          }`}
        >
          <div className="flex justify-between items-start">
            <div>
              <h2 className="text-lg font-bold text-gray-800">{ticket.incident_title}</h2>
              <p className="text-gray-600 text-sm">
                <b>ID:</b> {ticket.id} | <b>Status:</b> {ticket.status} | <b>Prioritate:</b> {ticket.priority_name}
              </p>
              <p className="text-gray-600 text-sm">
                <b>Proiect:</b> {ticket.project || "-"} | <b>Start:</b> {ticket.start_date}
              </p>
            </div>

            <button
              onClick={() => toggleExpand(ticket.id)}
              className="text-sm px-3 py-1 rounded-full bg-blue-600 text-white hover:bg-blue-700 transition"
            >
              {expandedIds.includes(ticket.id) ? "Ascunde -" : "Detalii +"}
            </button>
          </div>

          {expandedIds.includes(ticket.id) && (
            <div className="mt-4 border-t pt-4 text-sm text-gray-700">
              <p><b>Mai multe detalii vor fi afișate aici (poți extinde dacă vrei)</b></p>
            </div>
          )}
        </div>
      ))}

      {visibleCount < tickets.length && (
        <div className="flex justify-center mt-6">
          <button
            onClick={loadMore}
            className="px-6 py-2 bg-gray-800 text-white text-sm rounded-lg hover:bg-gray-700 transition"
          >
            Afișează mai multe
          </button>
        </div>
      )}
    </div>
  );
}
