import {
  PieChart,
  Pie,
  Cell,
  Tooltip,
  ResponsiveContainer
} from "recharts";

const STATUS_COLORS = {
  Open: "#3B82F6",
  "In Progress": "#FBBF24",
  Pending: "#F87171",
  Closed: "#10B981",
  Resolved: "#8B5CF6"
};

export default function TicketStatusPieChart({ data = [], height = 300 }) {
  return (
    <ResponsiveContainer width="100%" height={height}>
      <PieChart>
        <Pie
          data={data}
          dataKey="value"
          nameKey="name"
          cx="50%"
          cy="50%"
          outerRadius={height / 3}
          label
        >
          {data.map((entry, i) => (
            <Cell
              key={i}
              fill={STATUS_COLORS[entry.name] || "#9CA3AF"}
            />
          ))}
        </Pie>
        <Tooltip formatter={(val) => [val, "Count"]} />
      </PieChart>
    </ResponsiveContainer>
  );
}
