import React from "react";

export default function TicketList({ tickets = [] }) {
  if (!tickets.length) {
    return (
      <div className="text-center text-gray-500 p-8">
        Nu există tickete pentru perioada selectată.
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 gap-4">
      {tickets.map((ticket) => (
        <div
          key={ticket.id}
          className={`p-4 bg-white rounded-lg shadow border-l-4 ${
            ticket.priority_name === "Critical"
              ? "border-red-600"
              : ticket.priority_name === "High"
              ? "border-orange-400"
              : ticket.priority_name === "Medium"
              ? "border-yellow-300"
              : "border-green-400"
          }`}
        >
          <h3 className="text-lg font-semibold text-gray-800">{ticket.incident_title}</h3>
          <p className="text-sm text-gray-600">
            Prioritate: {ticket.priority_name} | Status: {ticket.status}
          </p>
          <p className="text-sm text-gray-500">
            Start: {ticket.start_date ? new Date(ticket.start_date).toLocaleDateString("ro-RO") : "-"}
          </p>
        </div>
      ))}
    </div>
  );
}
