import { useState } from "react";

export default function TicketList({ tickets }) {
  const [expandedTicketId, setExpandedTicketId] = useState(null);
  const [visibleCount, setVisibleCount] = useState(20); // Inițial 20 tickete
  const [searchTerm, setSearchTerm] = useState("");

  const toggleExpand = (id) => {
    setExpandedTicketId(expandedTicketId === id ? null : id);
  };

  const loadMore = () => {
    setVisibleCount((prev) => prev + 20); // Încarcă câte 20 tickete în plus
  };

  const priorityColors = {
    Critical: "border-red-700 bg-red-100 hover:bg-red-200",
    High: "border-orange-600 bg-orange-100 hover:bg-orange-200",
    Medium: "border-yellow-500 bg-yellow-100 hover:bg-yellow-200",
    Low: "border-green-600 bg-green-100 hover:bg-green-200",
  };

  const filteredTickets = tickets.filter((ticket) =>
    ticket.incident_title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ticket.id?.toString().includes(searchTerm)
  );

  return (
    <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-lg font-semibold text-gray-700 dark:text-gray-300">Lista Tickete</h2>
        <input
          type="text"
          placeholder="Caută după titlu sau ID..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="p-2 border rounded w-64 text-sm"
        />
      </div>

      {filteredTickets.length === 0 ? (
        <p className="text-center text-gray-400">Nu există tickete.</p>
      ) : (
        <>
          <ul className="space-y-4">
            {filteredTickets.slice(0, visibleCount).map((ticket) => (
              <li
                key={ticket.id}
                className={`p-4 border-l-4 rounded-lg shadow transition-all duration-300 cursor-pointer ${
                  priorityColors[ticket.priority_name] || "border-gray-400 bg-gray-100 hover:bg-gray-200"
                }`}
                onClick={() => toggleExpand(ticket.id)}
              >
                <div className="flex justify-between items-center">
                  <div>
                    <p className="font-bold text-gray-800">{ticket.incident_title}</p>
                    <p className="text-sm text-gray-600">{ticket.status}</p>
                  </div>
                  <button
                    className="text-blue-500 hover:text-blue-700 font-semibold text-sm"
                    onClick={(e) => {
                      e.stopPropagation();
                      toggleExpand(ticket.id);
                    }}
                  >
                    {expandedTicketId === ticket.id ? "Ascunde -" : "Detalii +"}
                  </button>
                </div>

                {/* Detalii extinse */}
                <div
                  className={`overflow-hidden transition-all duration-500 ${
                    expandedTicketId === ticket.id ? "max-h-[1000px] opacity-100 mt-4" : "max-h-0 opacity-0"
                  }`}
                >
                  <div className="p-4 mt-2 rounded bg-gray-100 dark:bg-gray-700 text-sm text-gray-700 dark:text-gray-300 shadow-inner">
                    <h3 className="font-semibold mb-2">Detalii Ticket:</h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <p><b>ID:</b> {ticket.id}</p>
                      <p><b>Incident Title:</b> {ticket.incident_title}</p>
                      <p><b>Status:</b> {ticket.status}</p>
                      <p><b>Priority:</b> {ticket.priority_name}</p>
                      <p><b>Start Date:</b> {ticket.start_date || "-"}</p>
                      <p><b>Closed Date:</b> {ticket.closed_date || "-"}</p>
                      <p><b>Assigned Person:</b> {ticket.assigned_person || "-"}</p>
                      <p><b>Project:</b> {ticket.project || "-"}</p>
                      <p><b>Last Modified:</b> {ticket.last_modified_date || "-"}</p>
                      <p className="md:col-span-2"><b>Descriere:</b> {ticket.description || "-"}</p>
                    </div>
                  </div>
                </div>
              </li>
            ))}
          </ul>

          {/* Buton Load More */}
          {visibleCount < filteredTickets.length && (
            <div className="flex justify-center mt-6">
              <button
                onClick={loadMore}
                className="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-full shadow text-sm"
              >
                Încarcă mai multe
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
}
