import { useEffect, useState } from "react";

const PRIORITY_COLORS = {
  Critical: "border-red-600",
  High: "border-orange-500",
  Medium: "border-yellow-400",
  Low: "border-green-400",
};

export default function Dashboard() {
  const [tickets, setTickets] = useState([]);
  const [expandedTicketId, setExpandedTicketId] = useState(null);
  const [filters, setFilters] = useState({ status: "", created_by: "", from_date: "", to_date: "" });

  useEffect(() => {
    const params = new URLSearchParams();
    if (filters.status) params.append("status", filters.status);
    if (filters.created_by) params.append("created_by", filters.created_by);
    if (filters.from_date && filters.to_date) {
      params.append("from_date", filters.from_date);
      params.append("to_date", filters.to_date);
    }

    fetch(`http://localhost/tickets-api/tickets_latest.php?${params.toString()}`)
      .then((res) => res.json())
      .then((data) => setTickets(data))
      .catch((err) => console.error("Eroare la fetch:", err));
  }, [filters]);

  const toggleExpand = (id) => {
    setExpandedTicketId(expandedTicketId === id ? null : id);
  };

  const formatDate = (date) => {
    if (!date) return "-";
    if (typeof date === "string") return date.split("T")[0];
    try {
      return new Date(date).toLocaleDateString();
    } catch {
      return "-";
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-100 to-gray-300 p-8">
      <h1 className="text-4xl font-extrabold mb-6 text-center text-gray-800">Tickete Deschise</h1>

      {/* Filtre */}
      <div className="flex flex-wrap justify-center gap-4 mb-8">
        <input
          type="text"
          placeholder="Filtru status"
          value={filters.status}
          onChange={(e) => setFilters({ ...filters, status: e.target.value })}
          className="px-4 py-2 rounded-md shadow-sm border border-gray-300"
        />
        <input
          type="text"
          placeholder="Filtru creator"
          value={filters.created_by}
          onChange={(e) => setFilters({ ...filters, created_by: e.target.value })}
          className="px-4 py-2 rounded-md shadow-sm border border-gray-300"
        />
        <input
          type="date"
          value={filters.from_date}
          onChange={(e) => setFilters({ ...filters, from_date: e.target.value })}
          className="px-4 py-2 rounded-md shadow-sm border border-gray-300"
        />
        <input
          type="date"
          value={filters.to_date}
          onChange={(e) => setFilters({ ...filters, to_date: e.target.value })}
          className="px-4 py-2 rounded-md shadow-sm border border-gray-300"
        />
      </div>

      {/* Tickete */}
      <div className="max-w-4xl mx-auto space-y-6">
        {tickets.map((ticket) => (
          <div
            key={ticket.id}
            className={`relative bg-white p-6 rounded-2xl shadow-lg border-l-8 ${PRIORITY_COLORS[ticket.priority_name] || "border-gray-300"}`}
          >
            <div className="flex justify-between items-start">
              <div>
                <h2 className="text-xl font-bold text-gray-800">{ticket.incident_title}</h2>
                <p className="text-sm text-gray-500">
                  <span className="font-medium">Prioritate:</span> {ticket.priority_name} |
                  <span className="ml-1 font-medium">Status:</span> {ticket.status}
                </p>
                <p className="text-sm text-gray-500">
                  <span className="font-medium">Creat de:</span> {ticket.created_by}
                </p>
                <p className="text-sm text-gray-500">
                  <span className="font-medium">Start:</span> {formatDate(ticket.start_date)}
                </p>
              </div>
              <button
                onClick={() => toggleExpand(ticket.id)}
                className="bg-blue-500 text-white text-xs px-3 py-1 rounded-md hover:bg-blue-600 transition"
              >
                {expandedTicketId === ticket.id ? "Ascunde" : "Detalii +"}
              </button>
            </div>

            {expandedTicketId === ticket.id && (
              <div className="mt-4 border-t pt-4 text-sm text-gray-700 space-y-1">
                <p><span className="font-medium">Echipă:</span> {ticket.team_assigned_person || "-"}</p>
                <p><span className="font-medium">Proiect:</span> {ticket.project || "-"}</p>
                <p><span className="font-medium">Comentariu:</span> {ticket.comment || "-"}</p>
                <p><span className="font-medium">Rezolvare:</span> {ticket.resolution || "-"}</p>
                <p><span className="font-medium">Închis la:</span> {formatDate(ticket.closed_date)}
                </p>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
