import { FaMoon, FaSun } from "react-icons/fa";

export default function ThemeToggle({ theme, toggleTheme }) {
  return (
    <div className="flex justify-end p-4">
      <button
        onClick={toggleTheme}
        className="flex items-center gap-2 bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200 px-4 py-2 rounded-full shadow hover:bg-gray-300 dark:hover:bg-gray-600 transition"
      >
        {theme === "light" ? (
          <>
            <FaMoon />
            Dark Mode
          </>
        ) : (
          <>
            <FaSun />
            Light Mode
          </>
        )}
      </button>
    </div>
  );
}
