import { useState } from "react";

export default function TicketList({ tickets }) {
  const [visibleCount, setVisibleCount] = useState(20);
  const [expandedIds, setExpandedIds] = useState([]);

  const toggleExpand = (id) => {
    setExpandedIds((prev) =>
      prev.includes(id) ? prev.filter((e) => e !== id) : [...prev, id]
    );
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow mb-8">
      <h2 className="text-lg font-semibold text-gray-700 mb-4">Lista Tickete</h2>

      <div className="flex flex-col gap-4">
        {tickets.slice(0, visibleCount).map((ticket) => (
          <div
            key={ticket.id}
            className="border rounded-lg p-4 shadow hover:shadow-md transition relative"
          >
            <div className="flex justify-between items-start">
              <div>
                <p className="font-bold text-gray-800">{ticket.incident_title}</p>
                <p className="text-sm text-gray-600">
                  <span className="font-semibold">Prioritate:</span> {ticket.priority_name} |{" "}
                  <span className="font-semibold">Status:</span> {ticket.status}
                </p>
              </div>
              <button
                onClick={() => toggleExpand(ticket.id)}
                className="text-blue-600 text-sm hover:underline"
              >
                {expandedIds.includes(ticket.id) ? "Ascunde -" : "Detalii +"}
              </button>
            </div>

            {expandedIds.includes(ticket.id) && (
              <div className="mt-4 text-sm text-gray-700 space-y-2">
                <div><strong>ID:</strong> {ticket.id}</div>
                <div><strong>Proiect:</strong> {ticket.project || "-"}</div>
                <div><strong>Start Date:</strong> {ticket.start_date || "-"}</div>
              </div>
            )}
          </div>
        ))}
      </div>

      {visibleCount < tickets.length && (
        <div className="flex justify-center mt-6">
          <button
            onClick={() => setVisibleCount((prev) => prev + 20)}
            className="px-6 py-2 bg-gray-800 text-white rounded hover:bg-gray-700 transition"
          >
            Afișează mai multe
          </button>
        </div>
      )}
    </div>
  );
}
