import { useState } from "react";

export default function TicketFilter({ onFilterChange }) {
  const [filters, setFilters] = useState({
    id: "",
    title: "",
    priority: "",
    status: "",
    startDateFrom: "",
    startDateTo: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    const newFilters = { ...filters, [name]: value };
    setFilters(newFilters);
    onFilterChange(newFilters); // trimitem filtrele spre Dashboard
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-4">
  <input
    type="text"
    placeholder="Caută după ID sau Nume"
    value={filters.search}
    onChange={(e) => onFilterChange({ ...filters, search: e.target.value })}
    className="border p-2 rounded w-full"
  />

  <input
    type="text"
    placeholder="Proiect"
    value={filters.project}
    onChange={(e) => onFilterChange({ ...filters, project: e.target.value })}
    className="border p-2 rounded w-full"
  />

  <input
    type="date"
    placeholder="Start Date"
    value={filters.dateFrom}
    onChange={(e) => onFilterChange({ ...filters, dateFrom: e.target.value })}
    className="border p-2 rounded w-full"
  />

  <select
    value={filters.priority}
    onChange={(e) => onFilterChange({ ...filters, priority: e.target.value })}
    className="border p-2 rounded w-full"
  >
    <option value="">Prioritate</option>
    <option value="Critical">Critical</option>
    <option value="High">High</option>
    <option value="Medium">Medium</option>
    <option value="Low">Low</option>
  </select>

  <select
    value={filters.status}
    onChange={(e) => onFilterChange({ ...filters, status: e.target.value })}
    className="border p-2 rounded w-full"
  >
    <option value="">Status</option>
    <option value="Open">Open</option>
    <option value="In Progress">In Progress</option>
    <option value="Resolved">Resolved</option>
    <option value="Closed">Closed</option>
  </select>
</div>
  );
}
