import React from "react";

const PRIORITY_COLORS = {
  Critical: "border-red-600 bg-red-50",
  High: "border-orange-400 bg-orange-50",
  Medium: "border-yellow-300 bg-yellow-50",
  Low: "border-green-400 bg-green-50",
};

export default function TicketList({ tickets }) {
  if (!Array.isArray(tickets) || tickets.length === 0) {
    return (
      <div className="text-center text-gray-500">
        Nu existÄƒ tickete disponibile.
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 gap-6">
      {tickets.map((ticket) => (
        <div
          key={ticket.id}
          className={`p-5 rounded-lg shadow-md border-l-8 transition-transform duration-200 transform hover:scale-[1.02] hover:shadow-lg ${PRIORITY_COLORS[ticket.priority_name] || "border-gray-300 bg-white"}`}
        >
          <div className="flex justify-between items-start">
            <div>
              <h3 className="text-lg font-semibold text-gray-800 mb-1">
                {ticket.incident_title}
              </h3>
              <p className="text-sm text-gray-600">
                <span className="font-semibold">Prioritate:</span> {ticket.priority_name} |{" "}
                <span className="font-semibold">Status:</span> {ticket.status}
              </p>
              <p className="text-sm text-gray-500 mt-1">
                <span className="font-semibold">Creat la:</span>{" "}
                {ticket.start_date ? new Date(ticket.start_date).toLocaleDateString("ro-RO") : "-"}
              </p>
            </div>

            <div className="flex flex-col items-end">
              <span className={`px-3 py-1 text-xs rounded-full font-bold ${
                ticket.priority_name === "Critical" ? "bg-red-600 text-white" :
                ticket.priority_name === "High" ? "bg-orange-500 text-white" :
                ticket.priority_name === "Medium" ? "bg-yellow-400 text-white" :
                "bg-green-400 text-white"
              }`}>
                {ticket.priority_name}
              </span>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}
