import { useState, useEffect } from "react";
import StatsCards from "./StatsCards";
import TicketFilter from "./TicketFilter";
import TicketList from "./TicketList";
import BarChart from "./BarChart";

export default function Dashboard() {
  const [stats, setStats] = useState({ total: 0, resolved: 0, avgTime: 0 });
  const [dailyData, setDailyData] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [filteredTickets, setFilteredTickets] = useState([]);
  const [activeTab, setActiveTab] = useState("active");
  const [period, setPeriod] = useState("month");
  const [loading, setLoading] = useState(true);

  const [filters, setFilters] = useState({
    search: "",
    priority: "",
    status: "",
    project: "",
    dateFrom: "",
    dateTo: ""
  });

  useEffect(() => {
    setLoading(true);
    fetch(`http://localhost/tickets-api/dashboard_stats.php?period=${period}`)
      .then((res) => res.json())
      .then((data) => {
        setStats(data.stats);
        setDailyData(data.dailyChart);
        setTickets(data.tickets);
        setFilteredTickets(data.tickets);
      })
      .catch((err) => console.error("Eroare la fetch:", err))
      .finally(() => setLoading(false));
  }, [period]);

  useEffect(() => {
    let filtered = [...tickets];
    if (filters.search) {
      const term = filters.search.toLowerCase();
      filtered = filtered.filter(t =>
        t.incident_title?.toLowerCase().includes(term) ||
        t.id?.toString().includes(term)
      );
    }
    if (filters.priority) filtered = filtered.filter(t => t.priority_name === filters.priority);
    if (filters.status) filtered = filtered.filter(t => t.status === filters.status);
    if (filters.project) filtered = filtered.filter(t => t.project?.toLowerCase().includes(filters.project.toLowerCase()));
    if (filters.dateFrom) filtered = filtered.filter(t => new Date(t.start_date) >= new Date(filters.dateFrom));
    if (filters.dateTo) filtered = filtered.filter(t => new Date(t.start_date) <= new Date(filters.dateTo));
    setFilteredTickets(filtered);
  }, [filters, tickets]);

  const displayedTickets = filteredTickets.filter(ticket => {
    if (activeTab === "active") return ticket.status !== "Resolved" && ticket.status !== "Closed";
    if (activeTab === "closed") return ticket.status === "Resolved" || ticket.status === "Closed";
    return true;
  });

  return (
    <div className="min-h-screen bg-gray-100 py-8 px-4 sm:px-10">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-800 mb-8 text-center">Dashboard Tickete</h1>

        {/* Statistici */}
        <StatsCards {...stats} />

        {/* Perioadă selecție */}
        <div className="bg-white p-4 rounded-lg shadow mb-6">
          <div className="flex space-x-4 justify-center">
            <button onClick={() => setPeriod("day")} className={`px-4 py-2 rounded ${period === "day" ? "bg-blue-500 text-white" : "bg-gray-200"}`}>Azi</button>
            <button onClick={() => setPeriod("week")} className={`px-4 py-2 rounded ${period === "week" ? "bg-blue-500 text-white" : "bg-gray-200"}`}>Săptămâna</button>
            <button onClick={() => setPeriod("month")} className={`px-4 py-2 rounded ${period === "month" ? "bg-blue-500 text-white" : "bg-gray-200"}`}>Luna</button>
          </div>
        </div>

        {/* Grafic pe zile */}
        <div className="bg-white p-6 rounded-lg shadow mb-8">
          {loading ? (
            <p className="text-center text-gray-500">Se încarcă graficul...</p>
          ) : (
            <BarChart data={dailyData} />
          )}
        </div>

        {/* Taburi: Active / Closed */}
        <div className="flex space-x-4 mb-6 justify-center">
          <button onClick={() => setActiveTab("active")} className={`px-4 py-2 rounded ${activeTab === "active" ? "bg-blue-500 text-white" : "bg-gray-300"}`}>Active</button>
          <button onClick={() => setActiveTab("closed")} className={`px-4 py-2 rounded ${activeTab === "closed" ? "bg-blue-500 text-white" : "bg-gray-300"}`}>Closed</button>
        </div>

        {/* Filtru Tickete */}
        <TicketFilter filters={filters} onFilterChange={(name, value) => setFilters(prev => ({ ...prev, [name]: value }))} />

        {/* Lista Tickete */}
        <TicketList tickets={displayedTickets} />
      </div>
    </div>
  );
}
