import { useEffect, useState } from "react";
import StatsCards from "./StatsCards";
import BarChart from "./BarChart";
import TicketFilter from "./TicketFilter";
import TicketList from "./TicketList";
import History from "./History";

export default function Dashboard() {
  const [activeTab, setActiveTab] = useState("active"); // active sau history
  const [stats, setStats] = useState({ total: 0, resolved: 0, avgTime: 0, critical: 0, pending: 0 });
  const [dailyData, setDailyData] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [filteredTickets, setFilteredTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    search: "",
    priority: "",
    status: "",
    project: "",
    dateFrom: "",
    dateTo: "",
  });

  useEffect(() => {
    setLoading(true);
    fetch("http://localhost/tickets-api/dashboard_stats.php?period=all")
      .then((res) => res.json())
      .then((data) => {
        setStats(data.stats || { total: 0, resolved: 0, avgTime: 0, critical: 0, pending: 0 });
        setDailyData(data.dailyChart || []);
        setTickets(data.tickets || []);
        setFilteredTickets(data.tickets || []);
      })
      .catch((err) => console.error("Eroare la fetch dashboard:", err))
      .finally(() => setLoading(false));
  }, []);

  useEffect(() => {
    if (!tickets) return;
    let filtered = [...tickets];

    if (filters.search.trim()) {
      const term = filters.search.toLowerCase();
      filtered = filtered.filter(
        (t) =>
          t.incident_title?.toLowerCase().includes(term) ||
          t.id?.toString().includes(term)
      );
    }

    if (filters.priority) {
      filtered = filtered.filter((t) => t.priority_name === filters.priority);
    }

    if (filters.status) {
      filtered = filtered.filter((t) => t.status === filters.status);
    }

    if (filters.project) {
      filtered = filtered.filter((t) =>
        t.project?.toLowerCase().includes(filters.project.toLowerCase())
      );
    }

    if (filters.dateFrom) {
      filtered = filtered.filter(
        (t) => new Date(t.start_date) >= new Date(filters.dateFrom)
      );
    }

    if (filters.dateTo) {
      filtered = filtered.filter(
        (t) => new Date(t.start_date) <= new Date(filters.dateTo)
      );
    }

    setFilteredTickets(filtered);
  }, [filters, tickets]);

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4 sm:px-10">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-800 mb-8 text-center">
          Dashboard Operațional Tickete
        </h1>

        {/* Tabs */}
        <div className="flex gap-6 mb-8 justify-center">
          <button
            onClick={() => setActiveTab("active")}
            className={`px-6 py-2 rounded-lg ${
              activeTab === "active" ? "bg-blue-600 text-white" : "bg-gray-300 text-gray-700"
            }`}
          >
            Tickete Active
          </button>
          <button
            onClick={() => setActiveTab("history")}
            className={`px-6 py-2 rounded-lg ${
              activeTab === "history" ? "bg-blue-600 text-white" : "bg-gray-300 text-gray-700"
            }`}
          >
            Istoric Tickete Închise
          </button>
        </div>

        {/* Loading */}
        {loading ? (
          <div className="flex justify-center items-center h-64">
            <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-blue-500 border-solid"></div>
          </div>
        ) : (
          <>
            {/* Active TAB */}
            {activeTab === "active" && (
              <>
                <StatsCards {...stats} />

                {/* Grafic tickete pe zile */}
                <div className="bg-white p-6 rounded-lg shadow mb-8">
                  <h2 className="text-lg font-semibold text-gray-700 mb-4">Tickete pe Zile</h2>
                  {dailyData.length > 0 ? (
                    <BarChart data={dailyData} />
                  ) : (
                    <p className="text-center text-gray-400">Nu există date pentru grafic.</p>
                  )}
                </div>

                {/* Filtrare tickete */}
                <div className="bg-white p-6 rounded-lg shadow mb-8">
                  <TicketFilter filters={filters} setFilters={setFilters} />
                </div>

                {/* Lista tickete */}
                <TicketList tickets={filteredTickets} />
              </>
            )}

            {/* History TAB */}
            {activeTab === "history" && (
              <>
                <History />
              </>
            )}
          </>
        )}
      </div>
    </div>
  );
}
