import { useState, useEffect } from "react";
import StatsCards from "./StatsCards";
import TicketList from "./TicketList";
import TicketStatusPieChart from "./TicketStatusPieChart";
import AverageTimePerTeamChart from "./AverageTimePerTeamChart";

export default function Dashboard() {
  const [period, setPeriod] = useState("day");
  const [loading, setLoading] = useState(true);

  const [stats, setStats] = useState({ total: 0, closed: 0, avgTime: 0 });
  const [dailyChart, setDailyChart] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [statusChart, setStatusChart] = useState([]);
  const [teamChart, setTeamChart] = useState([]);

  const [filters, setFilters] = useState({
    search: "",
    priority: "",
    status: "",
    project: "",
  });

  useEffect(() => {
    let filtered = [...tickets];
  
    if (filters.search.trim() !== "") {
      const term = filters.search.toLowerCase();
      filtered = filtered.filter(
        (t) =>
          t.incident_title?.toLowerCase().includes(term) ||
          t.id?.toString().includes(term)
      );
    }
  
    if (filters.priority) {
      filtered = filtered.filter((t) => t.priority_name === filters.priority);
    }
  
    if (filters.status) {
      filtered = filtered.filter((t) => t.status === filters.status);
    }
  
    if (filters.project) {
      filtered = filtered.filter((t) =>
        t.project?.toLowerCase().includes(filters.project.toLowerCase())
      );
    }
  
    if (filters.assigned_person) {
      filtered = filtered.filter((t) =>
        t.assigned_person?.toLowerCase().includes(filters.assigned_person.toLowerCase())
      );
    }
  
    if (filters.dateFrom) {
      filtered = filtered.filter(
        (t) => new Date(t.start_date) >= new Date(filters.dateFrom)
      );
    }
  
    if (filters.dateTo) {
      filtered = filtered.filter(
        (t) => new Date(t.start_date) <= new Date(filters.dateTo)
      );
    }
  
    if (filters.last_modified) {
      filtered = filtered.filter(
        (t) => t.last_modified_date?.includes(filters.last_modified)
      );
    }
  
    setFilteredTickets(filtered);
  }, [filters, tickets]);
  
  const filteredTickets = tickets.filter((t) => {
    const searchMatch =
      filters.search === "" ||
      t.incident_title?.toLowerCase().includes(filters.search.toLowerCase()) ||
      t.id?.toString().includes(filters.search);

    const priorityMatch = filters.priority === "" || t.priority_name === filters.priority;
    const statusMatch = filters.status === "" || t.status === filters.status;
    const projectMatch = filters.project === "" || (t.project && t.project.toLowerCase().includes(filters.project.toLowerCase()));

    return searchMatch && priorityMatch && statusMatch && projectMatch;
  });

  return (
    <div className="p-6">
      <div className="flex justify-center gap-4 mb-8">
        {["day", "week", "month", "year"].map((p) => (
          <button
            key={p}
            onClick={() => setPeriod(p)}
            className={`px-4 py-2 text-sm rounded-full ${
              period === p
                ? "bg-blue-600 text-white"
                : "bg-gray-300 dark:bg-gray-700 text-gray-800 dark:text-gray-300"
            }`}
          >
            {p === "day" && "Azi"}
            {p === "week" && "Săptămână"}
            {p === "month" && "Lună"}
            {p === "year" && "An"}
          </button>
        ))}
      </div>

      <StatsCards stats={stats} />

      {/* 2 grafice */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
          <h2 className="text-lg font-semibold mb-4 text-gray-700 dark:text-gray-300">Status Tickete</h2>
          {loading ? <p>Se încarcă...</p> : <TicketStatusPieChart data={statusChart} />}
        </div>
        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
          <h2 className="text-lg font-semibold mb-4 text-gray-700 dark:text-gray-300">Timp Mediu Rezolvare pe Echipă</h2>
          {loading ? <p>Se încarcă...</p> : <AverageTimePerTeamChart data={teamChart} />}
        </div>
      </div>

      {/* Filtrare Avansată */}
<div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow mb-8">
  <h2 className="text-lg font-semibold mb-4 text-gray-700 dark:text-gray-300">
    Filtrare Tickete
  </h2>

  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
    {/* ID sau Titlu */}
    <input
      type="text"
      placeholder="Căutare după ID sau Titlu"
      value={filters.search}
      onChange={(e) => setFilters({ ...filters, search: e.target.value })}
      className="p-2 border rounded"
    />

    {/* Prioritate */}
    <select
      value={filters.priority}
      onChange={(e) => setFilters({ ...filters, priority: e.target.value })}
      className="p-2 border rounded"
    >
      <option value="">Toate Prioritățile</option>
      <option value="Critical">Critical</option>
      <option value="High">High</option>
      <option value="Medium">Medium</option>
      <option value="Low">Low</option>
    </select>

    {/* Status */}
    <select
      value={filters.status}
      onChange={(e) => setFilters({ ...filters, status: e.target.value })}
      className="p-2 border rounded"
    >
      <option value="">Toate Statusurile</option>
      <option value="Open">Open</option>
      <option value="In Progress">In Progress</option>
      <option value="Pending">Pending</option>
      <option value="Closed">Closed</option>
    </select>

    {/* Assigned Person */}
    <input
      type="text"
      placeholder="Assigned Person"
      value={filters.assigned_person}
      onChange={(e) => setFilters({ ...filters, assigned_person: e.target.value })}
      className="p-2 border rounded"
    />
    
    {/* Project */}
    <input
      type="text"
      placeholder="Project"
      value={filters.project}
      onChange={(e) => setFilters({ ...filters, project: e.target.value })}
      className="p-2 border rounded"
    />

    {/* Start Date (de la) */}
    <input
      type="date"
      value={filters.dateFrom}
      onChange={(e) => setFilters({ ...filters, dateFrom: e.target.value })}
      className="p-2 border rounded"
    />

    {/* Start Date (până la) */}
    <input
      type="date"
      value={filters.dateTo}
      onChange={(e) => setFilters({ ...filters, dateTo: e.target.value })}
      className="p-2 border rounded"
    />

    {/* Last Modified Date */}
    <input
      type="text"
      placeholder="Last Modified Date"
      value={filters.last_modified}
      onChange={(e) => setFilters({ ...filters, last_modified: e.target.value })}
      className="p-2 border rounded"
    />
  </div>
</div>


      {/* Lista Tickete */}
      {loading ? (
        <p className="text-center text-gray-400">Se încarcă ticketele...</p>
      ) : (
        <TicketList tickets={filteredTickets} />
      )}
    </div>
  );
}
