import React from "react";
import { Pie } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js";

ChartJS.register(ArcElement, Tooltip, Legend);

export default function TicketStatusPieChart({ data }) {
  if (!data || data.length === 0) return <p>Nu existÄƒ date pentru grafic.</p>;

  const chartData = {
    labels: data.map((d) => d.status),
    datasets: [
      {
        label: "Nr. Tickete",
        data: data.map((d) => d.count),
        backgroundColor: [
          "#2563eb", // Open - blue
          "#22c55e", // In Progress - green
          "#facc15", // Pending - yellow
          "#ef4444", // Closed - red
          "#8b5cf6", // Custom - violet
          "#f97316", // Custom - orange
        ],
        borderColor: "white",
        borderWidth: 2,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: "bottom",
        labels: {
          color: "#6b7280", // text color
        },
      },
      tooltip: {
        callbacks: {
          label: function (context) {
            const total = context.dataset.data.reduce((a, b) => a + b, 0);
            const value = context.raw;
            const percentage = ((value / total) * 100).toFixed(1) + "%";
            return `${context.label}: ${value} (${percentage})`;
          },
        },
      },
    },
    layout: {
      padding: 20,
    },
  };

  return (
    <div className="relative w-full" style={{ height: "300px" }}>
      <Pie data={chartData} options={options} />
    </div>
  );
}
