import { useState } from "react";

const PRIORITY_COLORS = {
  Critical: "border-red-600 bg-red-50",
  High:     "border-orange-400 bg-orange-50",
  Medium:   "border-yellow-300 bg-yellow-50",
  Low:      "border-green-400 bg-green-50"
};

export default function TicketList({ tickets }) {
  const [expandedIds, setExpandedIds] = useState([]);
  const [visibleCount, setVisibleCount] = useState(20);

  const toggle = (id) =>
    setExpandedIds((prev) =>
      prev.includes(id) ? prev.filter(x => x !== id) : [...prev, id]
    );

  return (
    <div className="flex flex-col gap-6">
      {tickets.slice(0, visibleCount).map((t) => (
        <div
          key={t.id}
          className={`bg-white shadow-lg border-l-8 rounded-lg p-6 transition-all duration-300 ${
            PRIORITY_COLORS[t.priority_name] || "border-gray-300 bg-white"
          }`}
        >
          <div className="flex justify-between items-start">
            <div>
              <h3 className="text-lg font-bold text-gray-800 mb-1">
                {t.incident_title}
              </h3>
              <p className="text-sm text-gray-600">
                <b>ID:</b> {t.id} | <b>Status:</b> {t.status} |{" "}
                <b>Prioritate:</b> {t.priority_name}
              </p>
              <p className="text-sm text-gray-600 mt-1">
                <b>Start:</b> {t.start_date}
              </p>
            </div>
            <button
              onClick={() => toggle(t.id)}
              className="text-sm px-3 py-1 rounded-full bg-blue-600 text-white hover:bg-blue-700 transition"
            >
              {expandedIds.includes(t.id) ? "Ascunde –" : "Detalii +"}
            </button>
          </div>

          {expandedIds.includes(t.id) && (
            <div className="mt-4 border-t pt-4 grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-gray-700">
              <div><b>Project:</b> {t.project || "-"}</div>
              <div><b>Assigned:</b> {t.assigned_person || "-"}</div>
              <div><b>Team:</b> {t.team_assigned_person || "-"}</div>
              <div><b>Comentariu:</b> {t.comment || "-"}</div>
              <div><b>Rezolvare:</b> {t.resolution || "-"}</div>
              <div><b>Last Modified:</b> {t.last_modified_date || "-"}</div>
              <div><b>Închis la:</b> {t.closed_date || "-"}</div>
            </div>
          )}
        </div>
      ))}

      {visibleCount < tickets.length && (
        <div className="flex justify-center mt-6">
          <button
            onClick={() => setVisibleCount((c) => c + 20)}
            className="px-6 py-2 bg-gray-800 text-white text-sm rounded-lg hover:bg-gray-700 transition"
          >
            Afișează mai multe
          </button>
        </div>
      )}
    </div>
  );
}
