import { useState } from "react";
import StatsCards from "./StatsCards";
import TicketFilter from "./TicketFilter";
import TicketList from "./TicketList";
import BarChart from "./BarChart";

export default function Dashboard() {
  const [tab, setTab] = useState("overview");

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Taburi */}
      <div className="flex justify-center gap-4 py-6 bg-white shadow">
        <button
          onClick={() => setTab("overview")}
          className={`px-6 py-2 rounded ${tab === "overview" ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-700"}`}
        >
          Dashboard
        </button>
        <button
          onClick={() => setTab("history")}
          className={`px-6 py-2 rounded ${tab === "history" ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-700"}`}
        >
          Istoric Tickete
        </button>
      </div>

      {tab === "overview" ? (
        <div className="max-w-7xl mx-auto p-8">
          <StatsCards />
          <BarChart />
          <TicketFilter />
          <TicketList />
        </div>
      ) : (
        <div className="max-w-7xl mx-auto p-8">
          <TicketList historyMode />
        </div>
      )}
    </div>
  );
}
