import { useEffect, useState } from "react";
import StatsCards from "./StatsCards";
import BarChart from "./BarChart";
import TicketList from "./TicketList";
import TimeFilter from "./TimeFilter";

export default function Dashboard() {
  const [stats, setStats] = useState({ total: 0, resolved: 0, avgTime: 0 });
  const [dailyData, setDailyData] = useState([]);
  const [timeFilter, setTimeFilter] = useState("month");
  const [tickets, setTickets] = useState([]);
  const [urgentTickets, setUrgentTickets] = useState([]);
  const [showHistory, setShowHistory] = useState(false);

  // Fetch general dashboard data
  useEffect(() => {
    fetch(`http://localhost/tickets-api/dashboard_stats.php?period=${timeFilter}`)
      .then((res) => res.json())
      .then((data) => {
        setStats(data.stats);
        setDailyData(data.dailyChart);
        setTickets(data.tickets);
      })
      .catch((err) => console.error("Eroare la fetch dashboard:", err));
  }, [timeFilter]);

  // Fetch urgent tickets live
  useEffect(() => {
    const fetchUrgent = () => {
      fetch("http://localhost/tickets-api/tickets_urgent.php")
        .then((res) => res.json())
        .then((data) => setUrgentTickets(data))
        .catch((err) => console.error("Eroare la urgent tickets:", err));
    };

    fetchUrgent();
    const interval = setInterval(fetchUrgent, 10000); // refresh la 10 secunde

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4 sm:px-10">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-800 mb-8 text-center">
          Dashboard Operațional Tickete
        </h1>

        {/* Statistici */}
        <StatsCards {...stats} />

        {/* Tickete urgente */}
        <div className="bg-white p-6 rounded-lg shadow mb-8">
          <h2 className="text-lg font-semibold text-red-600 mb-4">
            Tickete Critice / Urgente (Live)
          </h2>
          {urgentTickets.length > 0 ? (
            <ul className="space-y-3">
              {urgentTickets.map((t) => (
                <li
                  key={t.id}
                  className={`p-4 border-l-4 rounded-lg shadow-sm ${
                    t.priority_name === "Critical"
                      ? "border-red-600 bg-red-50"
                      : "border-orange-400 bg-orange-50"
                  }`}
                >
                  <div className="font-bold text-gray-800">{t.incident_title}</div>
                  <div className="text-sm text-gray-600">
                    Prioritate: <b>{t.priority_name}</b> | Status: {t.status}
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p className="text-gray-500">Nu sunt tickete urgente în acest moment.</p>
          )}
        </div>

        {/* Grafic pe zile */}
        <div className="bg-white p-6 rounded-lg shadow mb-8">
          <h2 className="text-lg font-semibold text-gray-700 mb-4">Tickete pe Zile</h2>
          <BarChart data={dailyData} />
        </div>

        {/* Filtru timp + Istoric toggle */}
        <div className="bg-white p-6 rounded-lg shadow mb-8 flex flex-col sm:flex-row sm:justify-between items-center">
          <TimeFilter selected={timeFilter} onChange={setTimeFilter} />
          <button
            onClick={() => setShowHistory(!showHistory)}
            className="mt-4 sm:mt-0 bg-gray-800 text-white px-4 py-2 rounded hover:bg-gray-700"
          >
            {showHistory ? "Ascunde Istoric" : "Afișează Istoric"}
          </button>
        </div>

        {/* Lista tickete */}
        {showHistory && <TicketList tickets={tickets} />}
      </div>
    </div>
  );
}
