import { useState, useEffect } from "react";

export default function AdminPanel() {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [newStatus, setNewStatus] = useState("");
  const [showAddForm, setShowAddForm] = useState(false);
  const [newTicket, setNewTicket] = useState({
    incident_title: "",
    project: "",
    priority_id: "",
    assigned_person: ""
  });

  const role = localStorage.getItem("role");

  useEffect(() => {
    fetchTickets();
  }, []);

  const fetchTickets = () => {
    setLoading(true);
    fetch("http://localhost/tickets-api/tickets.php")
      .then((res) => res.json())
      .then((data) => setTickets(data || []))
      .catch((err) => console.error("Eroare la fetch tickete:", err))
      .finally(() => setLoading(false));
  };

  const handleUpdateStatus = (ticketId) => {
    if (!newStatus) return;

    fetch("http://localhost/tickets-api/update_ticket.php", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ id: ticketId, status: newStatus }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) {
          alert("Status actualizat!");
          fetchTickets();
          setSelectedTicket(null);
          setNewStatus("");
        } else {
          alert("Eroare la actualizare status.");
        }
      })
      .catch((err) => {
        console.error("Eroare update:", err);
        alert("Eroare server!");
      });
  };

  const handleDeleteTicket = (ticketId) => {
    if (!window.confirm("Sigur dorești să ștergi acest ticket?")) return;

    fetch(`http://localhost/tickets-api/delete_ticket.php?id=${ticketId}`, {
      method: "DELETE",
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) {
          alert("Ticket șters!");
          fetchTickets();
        } else {
          alert("Eroare la ștergere.");
        }
      })
      .catch((err) => {
        console.error("Eroare ștergere:", err);
        alert("Eroare server!");
      });
  };

  const handleAddTicket = (e) => {
    e.preventDefault();

    fetch("http://localhost/tickets-api/add_ticket.php", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newTicket),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) {
          alert("Ticket adăugat!");
          fetchTickets();
          setShowAddForm(false);
          setNewTicket({
            incident_title: "",
            project: "",
            priority_id: "",
            assigned_person: "",
          });
        } else {
          alert("Eroare la adăugare ticket.");
        }
      })
      .catch((err) => {
        console.error("Eroare adăugare:", err);
        alert("Eroare server!");
      });
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-8 text-center text-gray-800 dark:text-gray-100">
        Admin Panel
      </h1>

      {/* Buton Adăugare Superuser */}
      {role === "superuser" && (
        <div className="flex justify-center mb-8">
          <button
            onClick={() => setShowAddForm(true)}
            className="px-6 py-2 bg-green-600 text-white rounded hover:bg-green-700"
          >
            Adaugă Ticket Nou
          </button>
        </div>
      )}

      {loading ? (
        <p className="text-center text-gray-400">Se încarcă ticketele...</p>
      ) : tickets.length > 0 ? (
        <div className="space-y-6">
          {tickets.map((ticket) => (
            <div
              key={ticket.id}
              className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow flex flex-col gap-2"
            >
              <div className="flex justify-between items-center">
                <div>
                  <h2 className="text-xl font-semibold text-gray-700 dark:text-gray-300">
                    #{ticket.id} - {ticket.incident_title}
                  </h2>
                  <p className="text-gray-500 dark:text-gray-400">
                    Status: <b>{ticket.status}</b>
                  </p>
                  <p className="text-gray-500 dark:text-gray-400">
                    Proiect: <b>{ticket.project}</b>
                  </p>
                </div>

                <div className="flex gap-2">
                  <button
                    onClick={() => setSelectedTicket(ticket)}
                    className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                  >
                    Modifică Status
                  </button>

                  {/* doar superuser poate șterge */}
                  {role === "superuser" && (
                    <button
                      onClick={() => handleDeleteTicket(ticket.id)}
                      className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
                    >
                      Șterge
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center text-gray-400">Nu există tickete.</p>
      )}

      {/* Modal pentru modificare status */}
      {selectedTicket && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white dark:bg-gray-800 p-8 rounded-lg shadow-lg w-full max-w-md">
            <h2 className="text-xl font-bold mb-4 text-gray-700 dark:text-gray-200">
              Modifică status pentru ticket #{selectedTicket.id}
            </h2>
            <select
              value={newStatus}
              onChange={(e) => setNewStatus(e.target.value)}
              className="w-full p-2 border rounded mb-4 dark:bg-gray-700 dark:text-white"
            >
              <option value="">Selectează un status</option>
              <option value="Open">Open</option>
              <option value="In Progress">In Progress</option>
              <option value="Pending">Pending</option>
              <option value="Closed">Closed</option>
            </select>
            <div className="flex gap-4 justify-end">
              <button
                onClick={() => setSelectedTicket(null)}
                className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
              >
                Anulează
              </button>
              <button
                onClick={() => handleUpdateStatus(selectedTicket.id)}
                className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
              >
                Salvează
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Modal pentru adăugare ticket */}
      {showAddForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white dark:bg-gray-800 p-8 rounded-lg shadow-lg w-full max-w-md">
            <h2 className="text-xl font-bold mb-6 text-gray-700 dark:text-gray-200">
              Adaugă Ticket Nou
            </h2>
            <form onSubmit={handleAddTicket} className="space-y-4">
              <input
                type="text"
                placeholder="Titlu Incident"
                value={newTicket.incident_title}
                onChange={(e) => setNewTicket({ ...newTicket, incident_title: e.target.value })}
                required
                className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
              />
              <input
                type="text"
                placeholder="Proiect"
                value={newTicket.project}
                onChange={(e) => setNewTicket({ ...newTicket, project: e.target.value })}
                required
                className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
              />
              <select
                value={newTicket.priority_id}
                onChange={(e) => setNewTicket({ ...newTicket, priority_id: e.target.value })}
                required
                className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
              >
                <option value="">Selectează Prioritate</option>
                <option value="1">Critical</option>
                <option value="2">High</option>
                <option value="3">Medium</option>
                <option value="4">Low</option>
              </select>
              <input
                type="text"
                placeholder="Assigned Person"
                value={newTicket.assigned_person}
                onChange={(e) => setNewTicket({ ...newTicket, assigned_person: e.target.value })}
                required
                className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
              />
              <div className="flex justify-end gap-4">
                <button
                  onClick={() => setShowAddForm(false)}
                  type="button"
                  className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
                >
                  Anulează
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
                >
                  Adaugă
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
