import { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Dashboard from "./components/Dashboard";
import History from "./components/History";
import AdminPanel from "./components/AdminPanel";
import Login from "./components/Login";
import ThemeToggle from "./components/ThemeToggle";

export default function App() {
  const [authenticated, setAuthenticated] = useState(false);
  const [role, setRole] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    const savedRole = localStorage.getItem("role");
    setAuthenticated(!!token);
    setRole(savedRole || "");
  }, []);

  const handleLoginSuccess = (userRole) => {
    setAuthenticated(true);
    setRole(userRole);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    setAuthenticated(false);
    setRole("");
  };

  if (!authenticated) {
    return <Login onLogin={handleLoginSuccess} />;
  }

  return (
    <Router>
      <div className="min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors duration-300">
        <div className="relative flex justify-center gap-4 py-6 bg-white dark:bg-gray-800 shadow">
          <button
            onClick={() => window.location.href = "/"}
            className="px-6 py-2 rounded bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300"
          >
            Dashboard
          </button>
          <button
            onClick={() => window.location.href = "/history"}
            className="px-6 py-2 rounded bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300"
          >
            Istoric
          </button>
          {(role === "admin" || role === "superuser") && (
            <button
              onClick={() => window.location.href = "/admin"}
              className="px-6 py-2 rounded bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300"
            >
              Admin Panel
            </button>
          )}
          <div className="absolute top-4 right-4 flex gap-4">
            <ThemeToggle />
            <button onClick={handleLogout} className="bg-red-500 text-white px-4 py-2 rounded">
              Logout
            </button>
          </div>
        </div>

        {/* Rute */}
        <Routes>
          <Route path="/" element={<Dashboard />} />
          <Route path="/history" element={<History />} />
          <Route path="/admin" element={
            (role === "admin" || role === "superuser") ? <AdminPanel /> : <Navigate to="/" />
          } />
          {/* Redirect la Dashboard dacÄƒ altceva */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
}
