import { useState, useEffect } from "react";

export default function AdminPanel() {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [newStatus, setNewStatus] = useState("");

  useEffect(() => {
    fetch("http://localhost/tickets-api/tickets.php")
      .then((res) => res.json())
      .then((data) => setTickets(data || []))
      .catch((err) => console.error("Eroare la fetch tickete:", err))
      .finally(() => setLoading(false));
  }, []);

  const handleUpdateStatus = (ticketId) => {
    if (!newStatus) return;

    fetch("http://localhost/tickets-api/update_ticket.php", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        id: ticketId,
        status: newStatus
      })
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) {
          alert("Status actualizat!");
          setTickets((prev) =>
            prev.map((ticket) =>
              ticket.id === ticketId ? { ...ticket, status: newStatus } : ticket
            )
          );
          setSelectedTicket(null);
          setNewStatus("");
        } else {
          alert("Eroare la actualizare status.");
        }
      })
      .catch((err) => {
        console.error("Eroare update:", err);
        alert("Eroare server!");
      });
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-8 text-center text-gray-800 dark:text-gray-100">
        Admin Panel - Management Tickete
      </h1>

      {loading ? (
        <p className="text-center text-gray-400">Se încarcă ticketele...</p>
      ) : tickets.length > 0 ? (
        <div className="space-y-6">
          {tickets.map((ticket) => (
            <div
              key={ticket.id}
              className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow flex flex-col gap-2"
            >
              <div className="flex justify-between items-center">
                <div>
                  <h2 className="text-xl font-semibold text-gray-700 dark:text-gray-300">
                    #{ticket.id} - {ticket.incident_title}
                  </h2>
                  <p className="text-gray-500 dark:text-gray-400">
                    Status curent: <b>{ticket.status}</b>
                  </p>
                </div>
                <button
                  onClick={() => setSelectedTicket(ticket)}
                  className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                >
                  Modifică Status
                </button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center text-gray-400">Nu există tickete momentan.</p>
      )}

      {/* Modal de schimbare status */}
      {selectedTicket && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white dark:bg-gray-800 p-8 rounded-lg shadow-lg w-full max-w-md">
            <h2 className="text-xl font-bold mb-4 text-gray-700 dark:text-gray-200">
              Schimbă statusul pentru ticket #{selectedTicket.id}
            </h2>
            <select
              value={newStatus}
              onChange={(e) => setNewStatus(e.target.value)}
              className="w-full p-2 border rounded mb-4 dark:bg-gray-700 dark:text-white"
            >
              <option value="">Selectează un status</option>
              <option value="Open">Open</option>
              <option value="In Progress">In Progress</option>
              <option value="Pending">Pending</option>
              <option value="Closed">Closed</option>
            </select>
            <div className="flex gap-4 justify-end">
              <button
                onClick={() => setSelectedTicket(null)}
                className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
              >
                Anulează
              </button>
              <button
                onClick={() => handleUpdateStatus(selectedTicket.id)}
                className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
              >
                Salvează
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
