import { useState } from "react";

export default function TicketList({ tickets }) {
  const [expandedTicketId, setExpandedTicketId] = useState(null);

  const toggleExpand = (id) => {
    setExpandedTicketId(expandedTicketId === id ? null : id);
  };

  const priorityColors = {
    Critical: "border-red-500 bg-red-50",
    High: "border-orange-500 bg-orange-50",
    Medium: "border-yellow-400 bg-yellow-50",
    Low: "border-green-400 bg-green-50",
  };

  return (
    <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
      <h2 className="text-lg font-semibold mb-4 text-gray-700 dark:text-gray-300">Lista Tickete</h2>
      {tickets.length === 0 ? (
        <p className="text-center text-gray-400">Nu existÄƒ tickete.</p>
      ) : (
        <ul className="divide-y divide-gray-200 dark:divide-gray-700">
          {tickets.map((ticket) => (
            <li
              key={ticket.id}
              className={`py-4 px-4 border-l-4 rounded-lg mb-4 ${
                priorityColors[ticket.priority_name] || "border-gray-400"
              }`}
            >
              <div className="flex justify-between items-center">
                <div>
                  <p className="font-semibold">{ticket.incident_title}</p>
                  <p className="text-sm text-gray-500">{ticket.status}</p>
                </div>
                <button
                  className="text-blue-500 hover:text-blue-700 font-semibold text-sm"
                  onClick={() => toggleExpand(ticket.id)}
                >
                  {expandedTicketId === ticket.id ? "Ascunde -" : "Detalii +"}
                </button>
              </div>

              {expandedTicketId === ticket.id && (
                <div className="p-4 bg-gray-50 dark:bg-gray-700 rounded mt-2">
                  <p><b>Descriere:</b> {ticket.description || "-"}</p>
                  <p><b>Prioritate:</b> {ticket.priority_name}</p>
                  <p><b>Start Date:</b> {ticket.start_date}</p>
                  <p><b>Closed Date:</b> {ticket.closed_date || "-"}</p>
                  <p><b>Status:</b> {ticket.status}</p>
                  <p><b>Assigned Person:</b> {ticket.assigned_person || "-"}</p>
                  <p><b>Project:</b> {ticket.project || "-"}</p>
                  <p><b>Last Modified:</b> {ticket.last_modified_date || "-"}</p>
                </div>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
