export default function TicketList({ tickets }) {
  const PRIORITY_COLORS = {
    Critical: "border-l-8 border-red-600",
    High: "border-l-8 border-orange-400",
    Medium: "border-l-8 border-yellow-400",
    Low: "border-l-8 border-green-400",
  };

  return (
    <div className="grid gap-4">
      {tickets.map((ticket) => (
        <div
          key={ticket.id}
          className={`bg-white p-4 rounded-lg shadow ${PRIORITY_COLORS[ticket.priority_name] || "border-l-8 border-gray-300"}`}
        >
          <div className="flex justify-between items-center">
            <div>
              <h3 className="font-bold text-lg">{ticket.incident_title}</h3>
              <p className="text-sm text-gray-600">
                <span className="font-semibold">Status:</span> {ticket.status} |{" "}
                <span className="font-semibold">Prioritate:</span> {ticket.priority_name} |{" "}
                <span className="font-semibold">Start:</span> {ticket.start_date?.split("T")[0]}
              </p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}
