import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from "react-router-dom";
import Dashboard from "./components/Dashboard";
import History from "./components/History";
import ThemeToggle from "./components/ThemeToggle";
import Login from "./components/Login";
import AdminPanel from "./components/AdminPanel";
import RequireAuth from "./hooks/RequireAuth";

export default function App() {
  const [activeTab, setActiveTab] = useState("dashboard");
  const [authenticated, setAuthenticated] = useState(false);
  const [role, setRole] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    const storedRole = localStorage.getItem("role");
    if (token) {
      setAuthenticated(true);
      setRole(storedRole);
    }
  }, []);

  const handleLoginSuccess = (userRole) => {
    setAuthenticated(true);
    setRole(userRole);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    setAuthenticated(false);
    setRole("");
  };

  if (!authenticated) {
    return <Login onLogin={handleLoginSuccess} />;
  }

  return (
    <Router>
      <div className="min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors duration-300">
        <div className="relative flex justify-center gap-4 py-6 bg-white dark:bg-gray-800 shadow">
          <button
            onClick={() => setActiveTab("dashboard")}
            className={`px-6 py-2 rounded ${activeTab === "dashboard" ? "bg-blue-600 text-white" : "bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300"}`}
          >
            Dashboard
          </button>
          <button
            onClick={() => setActiveTab("history")}
            className={`px-6 py-2 rounded ${activeTab === "history" ? "bg-blue-600 text-white" : "bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300"}`}
          >
            Istoric
          </button>

          {/* DACA E ADMIN SAU SUPERUSER -> vezi Admin Panel */}
          {(role === "admin" || role === "superuser") && (
            <Link to="/admin">
              <button className="px-6 py-2 bg-purple-600 text-white rounded hover:bg-purple-700">
                Admin Panel
              </button>
            </Link>
          )}

          <div className="absolute top-4 right-4 flex gap-4">
            <ThemeToggle />
            <button onClick={handleLogout} className="bg-red-500 text-white px-4 py-2 rounded">
              Logout
            </button>
          </div>
        </div>

        {/* Rute */}
        <Routes>
          <Route path="/" element={activeTab === "dashboard" ? <Dashboard /> : <History />} />
          <Route
            path="/admin"
            element={
              <RequireAuth allowedRoles={["admin", "superuser"]}>
                <AdminPanel />
              </RequireAuth>
            }
          />
        </Routes>
      </div>
    </Router>
  );
}
