import { useState } from "react";

export default function TicketList({ tickets = [], historyMode = false }) {
  const [visibleCount, setVisibleCount] = useState(20);

  const showMore = () => {
    setVisibleCount((prev) => prev + 20);
  };

  if (!tickets.length) {
    return <p className="text-center text-gray-500 mt-10">Nu există tickete.</p>;
  }

  return (
    <div className="bg-white p-6 rounded-lg shadow">
      <div className="overflow-x-auto">
        <table className="min-w-full text-sm text-gray-700">
          <thead>
            <tr className="bg-gray-100 text-left">
              <th className="p-3">ID</th>
              <th className="p-3">Titlu</th>
              <th className="p-3">Status</th>
              <th className="p-3">Prioritate</th>
              <th className="p-3">Proiect</th>
              <th className="p-3">Data Start</th>
              {historyMode && <th className="p-3">Data Închidere</th>}
            </tr>
          </thead>
          <tbody>
            {tickets.slice(0, visibleCount).map((t) => (
              <tr key={t.id} className="border-b">
                <td className="p-3">{t.id}</td>
                <td className="p-3">{t.incident_title}</td>
                <td className="p-3">{t.status}</td>
                <td className="p-3">{t.priority_name}</td>
                <td className="p-3">{t.project || "-"}</td>
                <td className="p-3">{t.start_date}</td>
                {historyMode && <td className="p-3">{t.closed_date || "-"}</td>}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {visibleCount < tickets.length && (
        <div className="flex justify-center mt-6">
          <button
            onClick={showMore}
            className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700"
          >
            Afișează mai multe
          </button>
        </div>
      )}
    </div>
  );
}
