import { useState, useEffect } from "react";

export default function AdminPanel() {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [editTicket, setEditTicket] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);

  const [newTicket, setNewTicket] = useState({
    incident_title: "",
    project: "",
    priority_id: "",
    assigned_person: "",
    status: "Open"
  });

  const role = localStorage.getItem("role");

  useEffect(() => {
    fetch("http://localhost/tickets-api/tickets.php")
      .then((res) => res.json())
      .then((data) => setTickets(data || []))
      .catch((err) => console.error("Eroare la fetch tickete:", err))
      .finally(() => setLoading(false));
  }, []);

  // Funcții
  const refreshTickets = () => {
    fetch("http://localhost/tickets-api/tickets.php")
      .then((res) => res.json())
      .then((data) => setTickets(data || []));
  };

  const handleEditTicket = (ticket) => {
    setEditTicket({ ...ticket });
  };

  const handleSaveEdit = () => {
    if (!editTicket.incident_title || !editTicket.project || !editTicket.priority_id || !editTicket.status) {
      alert("Completează toate câmpurile!");
      return;
    }

    fetch("http://localhost/tickets-api/update_ticket.php", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(editTicket)
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) {
          alert("✅ Ticket actualizat cu succes!");
          setEditTicket(null);
          refreshTickets();
        } else {
          alert("Eroare la actualizare!");
        }
      })
      .catch((err) => {
        console.error("Eroare update:", err);
        alert("Eroare server!");
      });
  };

  const handleDeleteTicket = (ticketId) => {
    if (!window.confirm("Sigur dorești să ștergi acest ticket?")) return;

    fetch(`http://localhost/tickets-api/delete_ticket.php?id=${ticketId}`, {
      method: "DELETE"
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) {
          alert("Ticket șters!");
          setTickets((prev) => prev.filter((t) => t.id !== ticketId));
        } else {
          alert("Eroare la ștergere.");
        }
      })
      .catch((err) => {
        console.error("Eroare ștergere:", err);
        alert("Eroare server!");
      });
  };

  const handleAddTicket = (e) => {
    e.preventDefault();

    fetch("http://localhost/tickets-api/add_ticket.php", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newTicket)
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) {
          alert("✅ Ticket adăugat cu succes!");
          setTickets((prev) => [data.ticket, ...prev]);
          setShowAddForm(false);
          setNewTicket({
            incident_title: "",
            project: "",
            priority_id: "",
            assigned_person: "",
            status: "Open"
          });
        } else {
          alert("Eroare la adăugare ticket.");
        }
      })
      .catch((err) => {
        console.error("Eroare adăugare:", err);
        alert("Eroare server!");
      });
  };

  // Calcule mini KPIs
  const totalTickets = tickets.length;
  const openTickets = tickets.filter((t) => t.status !== "Closed").length;
  const closedTickets = tickets.filter((t) => t.status === "Closed").length;

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-8 text-center text-gray-800 dark:text-gray-100">
        Admin Panel - Management Tickete
      </h1>

      {/* Mini Dashboard KPIs */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12">
        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow text-center">
          <h2 className="text-2xl font-bold text-blue-600">{totalTickets}</h2>
          <p className="text-gray-500 dark:text-gray-400">Total Tickete</p>
        </div>
        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow text-center">
          <h2 className="text-2xl font-bold text-green-500">{openTickets}</h2>
          <p className="text-gray-500 dark:text-gray-400">Tickete Deschise</p>
        </div>
        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow text-center">
          <h2 className="text-2xl font-bold text-red-500">{closedTickets}</h2>
          <p className="text-gray-500 dark:text-gray-400">Tickete Închise</p>
        </div>
      </div>

      {/* Buton adaugare doar pentru superuser */}
      {role === "superuser" && (
        <div className="flex justify-center mb-8">
          <button
            onClick={() => setShowAddForm(true)}
            className="px-6 py-2 bg-green-600 text-white rounded hover:bg-green-700"
          >
            Adaugă Ticket Nou
          </button>
        </div>
      )}

      {loading ? (
        <p className="text-center text-gray-400">Se încarcă ticketele...</p>
      ) : (
        <div className="space-y-6">
          {tickets.map((ticket) => (
            <div key={ticket.id} className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
              <div className="flex justify-between items-center">
                <div>
                  <h2 className="text-xl font-semibold text-gray-700 dark:text-gray-300">
                    #{ticket.id} - {ticket.incident_title}
                  </h2>
                  <p className="text-gray-500 dark:text-gray-400">
                    Status: <b>{ticket.status}</b>
                  </p>
                </div>
                <div className="flex gap-2">
                  {(role === "admin" || role === "superuser") && (
                    <button
                      onClick={() => handleEditTicket(ticket)}
                      className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                    >
                      Editează
                    </button>
                  )}
                  {role === "superuser" && (
                    <button
                      onClick={() => handleDeleteTicket(ticket.id)}
                      className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
                    >
                      Șterge
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Modal Editare Ticket */}
      {editTicket && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 transition">
          <div className="bg-white dark:bg-gray-800 p-8 rounded-lg shadow-lg w-full max-w-lg animate-fade-in">
            <h2 className="text-xl font-bold mb-6 text-gray-700 dark:text-gray-200 text-center">
              Editează Ticket #{editTicket.id}
            </h2>
            <div className="space-y-4">
              <input
                type="text"
                value={editTicket.incident_title}
                onChange={(e) => setEditTicket({ ...editTicket, incident_title: e.target.value })}
                placeholder="Titlu Incident"
                className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
              />
              <input
                type="text"
                value={editTicket.project}
                onChange={(e) => setEditTicket({ ...editTicket, project: e.target.value })}
                placeholder="Proiect"
                className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
              />
              <select
                value={editTicket.priority_id}
                onChange={(e) => setEditTicket({ ...editTicket, priority_id: e.target.value })}
                className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
              >
                <option value="">Selectează Prioritate</option>
                <option value="1">Critical</option>
                <option value="2">High</option>
                <option value="3">Medium</option>
                <option value="4">Low</option>
              </select>
              <input
                type="text"
                value={editTicket.assigned_person}
                onChange={(e) => setEditTicket({ ...editTicket, assigned_person: e.target.value })}
                placeholder="Assigned Person"
                className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
              />
              <select
                value={editTicket.status}
                onChange={(e) => setEditTicket({ ...editTicket, status: e.target.value })}
                className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
              >
                <option value="">Selectează Status</option>
                <option value="Open">Open</option>
                <option value="In Progress">In Progress</option>
                <option value="Pending">Pending</option>
                <option value="Closed">Closed</option>
              </select>
              <div className="flex justify-end gap-4 mt-4">
                <button
                  onClick={() => setEditTicket(null)}
                  className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
                >
                  Anulează
                </button>
                <button
                  onClick={handleSaveEdit}
                  className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
                >
                  Salvează
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Modal Adaugă Ticket - deja îl avem */}
      {showAddForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 transition">
          <div className="bg-white dark:bg-gray-800 p-8 rounded-lg shadow-lg w-full max-w-lg animate-fade-in">
            <h2 className="text-xl font-bold mb-6 text-gray-700 dark:text-gray-200 text-center">
              Adaugă Ticket Nou
            </h2>
            <form onSubmit={handleAddTicket} className="space-y-4">
              <input
                type="text"
                value={newTicket.incident_title}
                onChange={(e) => setNewTicket({ ...newTicket, incident_title: e.target.value })}
                placeholder="Titlu Incident"
                className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
                required
              />
              <input
                type="text"
                value={newTicket.project}
                onChange={(e) => setNewTicket({ ...newTicket, project: e.target.value })}
                placeholder="Proiect"
                className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
                required
              />
              <select
                value={newTicket.priority_id}
                onChange={(e) => setNewTicket({ ...newTicket, priority_id: e.target.value })}
                className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
                required
              >
                <option value="">Selectează Prioritate</option>
                <option value="1">Critical</option>
                <option value="2">High</option>
                <option value="3">Medium</option>
                <option value="4">Low</option>
              </select>
              <input
                type="text"
                value={newTicket.assigned_person}
                onChange={(e) => setNewTicket({ ...newTicket, assigned_person: e.target.value })}
                placeholder="Assigned Person"
                className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
                required
              />
              <div className="flex justify-end gap-4 mt-4">
                <button
                  onClick={() => setShowAddForm(false)}
                  type="button"
                  className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
                >
                  Anulează
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
                >
                  Adaugă
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
