import { useEffect, useState } from "react";

const PRIORITY_COLORS = {
  Critical: "bg-red-600 text-white",
  High: "bg-orange-500 text-white",
  Medium: "bg-yellow-400 text-black",
  Low: "bg-green-400 text-black",
};

export default function Dashboard() {
  const [tickets, setTickets] = useState([]);

  useEffect(() => {
    fetch("http://localhost/tickets-api/tickets_latest.php")
      .then((res) => res.json())
      .then((data) => setTickets(data))
      .catch((err) => console.error("Eroare la fetch:", err));
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6">Ultimele 20 tickete</h1>

      <div className="overflow-x-auto">
        <table className="min-w-full bg-white shadow-md rounded-xl">
          <thead>
            <tr className="bg-gray-100 text-left text-sm text-gray-700 uppercase">
              <th className="p-3">Titlu</th>
              <th className="p-3">Prioritate</th>
              <th className="p-3">Status</th>
              <th className="p-3">Creat de</th>
              <th className="p-3">DatÄƒ</th>
              <th className="p-3">Proiect</th>
            </tr>
          </thead>
          <tbody>
            {tickets.map((ticket) => (
              <tr key={ticket.id} className="border-t hover:bg-gray-50">
                <td className="p-3">{ticket.incident_title}</td>
                <td className="p-3">
                  <span className={`px-2 py-1 rounded-full text-xs font-semibold ${PRIORITY_COLORS[ticket.priority_name] || 'bg-gray-200'}`}>
                    {ticket.priority_name}
                  </span>
                </td>
                <td className="p-3">{ticket.status}</td>
                <td className="p-3">{ticket.created_by}</td>
                <td className="p-3">{ticket.start_date?.split("T")[0]}</td>
                <td className="p-3">{ticket.project}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
