import { useState } from "react";
import Dashboard from "./components/Dashboard";
import History from "./components/History";
import ThemeToggle from "./components/ThemeToggle";

export default function App() {
  const [activeTab, setActiveTab] = useState("dashboard");

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors duration-300">
      {/* Bara de sus cu taburi + ThemeToggle */}
      <div className="relative flex justify-center gap-4 py-6 bg-white dark:bg-gray-800 shadow">
        {/* Butoane tab */}
        <button
          onClick={() => setActiveTab("dashboard")}
          className={`px-6 py-2 rounded ${activeTab === "dashboard" ? "bg-blue-600 text-white" : "bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300"}`}
        >
          Dashboard
        </button>
        <button
          onClick={() => setActiveTab("history")}
          className={`px-6 py-2 rounded ${activeTab === "history" ? "bg-blue-600 text-white" : "bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300"}`}
        >
          Istoric
        </button>

        {/* Buton ThemeToggle */}
        <div className="absolute top-4 right-4">
          <ThemeToggle />
        </div>
      </div>

      {/* Con»õinut Taburi */}
      {activeTab === "dashboard" ? <Dashboard /> : <History />}
    </div>
  );
}
