import { useEffect, useState } from "react";
import TicketList from "./TicketList";

export default function History() {
  const [closedTickets, setClosedTickets] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    fetch("http://localhost/tickets-api/tickets.php?status=Closed")
      .then((res) => res.json())
      .then((data) => {
        setClosedTickets(data || []);
      })
      .catch((err) => console.error("Eroare la fetch history:", err))
      .finally(() => setLoading(false));
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4 sm:px-10">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-800 mb-8 text-center">
          Istoric Tickete Închise
        </h1>

        {loading ? (
          <p className="text-center text-gray-500">Se încarcă datele...</p>
        ) : closedTickets.length > 0 ? (
          <TicketList tickets={closedTickets} />
        ) : (
          <p className="text-center text-gray-500">Nu există tickete închise.</p>
        )}
      </div>
    </div>
  );
}
