import { useEffect, useState } from "react";
import StatsCards from "./StatsCards";
import BarChart from "./BarChart";
import TimeFilter from "./TimeFilter";
import TicketList from "./TicketList";

export default function Dashboard() {
  const [stats, setStats] = useState({ total: 0, resolved: 0, avgTime: 0 });
  const [dailyData, setDailyData] = useState([]);
  const [timeFilter, setTimeFilter] = useState("month");
  const [tickets, setTickets] = useState([]);
  const [filteredTickets, setFilteredTickets] = useState([]);
  const [search, setSearch] = useState("");
  const [priorityFilter, setPriorityFilter] = useState("");
  const [dateFrom, setDateFrom] = useState("");
  const [dateTo, setDateTo] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    fetch('http://localhost/tickets-api/dashboard_stats.php')
      .then((res) => res.json())
      .then((data) => {
        setStats(data.stats || { total: 0, resolved: 0, avgTime: 0 });
        setDailyData(data.dailyChart || []);
        setTickets(data.tickets || []);
        setFilteredTickets(data.tickets || []);
      })
      .catch((err) => console.error("Eroare la fetch dashboard:", err))
      .finally(() => setLoading(false));
  }, [timeFilter]);

  useEffect(() => {
    if (!Array.isArray(tickets)) return;

    let filtered = [...tickets];

    if (search.trim() !== "") {
      filtered = filtered.filter(
        (ticket) =>
          ticket.incident_title?.toLowerCase().includes(search.toLowerCase()) ||
          ticket.id?.toString().includes(search)
      );
    }

    if (priorityFilter) {
      filtered = filtered.filter((ticket) => ticket.priority_name === priorityFilter);
    }

    if (dateFrom) {
      filtered = filtered.filter((ticket) => new Date(ticket.start_date) >= new Date(dateFrom));
    }

    if (dateTo) {
      filtered = filtered.filter((ticket) => new Date(ticket.start_date) <= new Date(dateTo));
    }

    setFilteredTickets(filtered);
  }, [search, priorityFilter, dateFrom, dateTo, tickets]);

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4 sm:px-10">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-800 mb-8 text-center">
          Dashboard Operațional Tickete
        </h1>
  
        {loading ? (
          <div className="flex flex-col items-center justify-center py-20">
            <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-gray-900 mb-4"></div>
            <p className="text-gray-500 text-lg">Se încarcă datele...</p>
          </div>
        ) : (
          <>
            {/* Statistici */}
            <StatsCards {...stats} />
  
            {/* Grafic pe zile */}
            <div className="bg-white p-6 rounded-lg shadow mb-8">
              <h2 className="text-lg font-semibold text-gray-700 mb-4">Tickete pe Zile</h2>
              <BarChart data={dailyData} />
            </div>
  
            {/* Filtru de căutare */}
            <div className="bg-white p-6 rounded-lg shadow mb-8">
              <h2 className="text-lg font-semibold text-gray-700 mb-4">Filtrare Tickete</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                <input
                  type="text"
                  placeholder="Caută după titlu sau ID"
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                  className="border p-2 rounded w-full"
                />
                <select
                  value={priorityFilter}
                  onChange={(e) => setPriorityFilter(e.target.value)}
                  className="border p-2 rounded w-full"
                >
                  <option value="">Toate Prioritățile</option>
                  <option value="Critical">Critical</option>
                  <option value="High">High</option>
                  <option value="Medium">Medium</option>
                  <option value="Low">Low</option>
                </select>
                <input
                  type="date"
                  value={dateFrom}
                  onChange={(e) => setDateFrom(e.target.value)}
                  className="border p-2 rounded w-full"
                />
                <input
                  type="date"
                  value={dateTo}
                  onChange={(e) => setDateTo(e.target.value)}
                  className="border p-2 rounded w-full"
                />
              </div>
            </div>
  
            {/* Lista Tickete */}
            {filteredTickets.length > 0 ? (
              <TicketList tickets={filteredTickets} />
            ) : (
              <p className="text-center text-gray-500">Nu există tickete pentru filtrele alese.</p>
            )}
          </>
        )}
      </div>
    </div>
  );
  
}
