import React from "react";

const StatsCards = ({ total, resolved, avgTime }) => {
  return (
    <div className="grid grid-cols-1 sm:grid-cols-3 gap-6 mb-8">
      <div className="bg-white p-6 rounded-lg shadow text-center border-l-4 border-blue-500">
        <h2 className="text-sm text-gray-500">Total Tickete</h2>
        <p className="text-2xl font-semibold text-gray-800">{total}</p>
      </div>
      <div className="bg-white p-6 rounded-lg shadow text-center border-l-4 border-green-500">
        <h2 className="text-sm text-gray-500">Tickete Rezolvate</h2>
        <p className="text-2xl font-semibold text-gray-800">{resolved}</p>
      </div>
      <div className="bg-white p-6 rounded-lg shadow text-center border-l-4 border-yellow-500">
        <h2 className="text-sm text-gray-500">Timp Mediu de Rezolvare</h2>
        <p className="text-2xl font-semibold text-gray-800">{avgTime}h</p>
      </div>
    </div>
  );
};

export default StatsCards;
